import React, { useState } from 'react';
import { PlusCircle } from 'lucide-react';
import RoleModal from './RoleModal';
import PermissionMatrix from './PermissionMatrix';
import { useRoles } from '../../hooks/useRoles';

const RoleList = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedRole, setSelectedRole] = useState(null);
  const { roles, addRole, updateRole, deleteRole } = useRoles();

  const handleAddRole = () => {
    setSelectedRole(null);
    setIsModalOpen(true);
  };

  const handleEditRole = (role) => {
    setSelectedRole(role);
    setIsModalOpen(true);
  };

  const handleSaveRole = (role) => {
    if (role.id) {
      updateRole(role);
    } else {
      addRole(role);
    }
    setIsModalOpen(false);
  };

  return (
    <div className="container mx-auto py-8 px-4">
      {/* Page Header */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            Roles & Permissions
          </h1>
          <p className="mt-2 text-gray-600 dark:text-gray-400">
            Manage user roles and their associated permissions
          </p>
        </div>
        <button
          onClick={handleAddRole}
          className="inline-flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors duration-200"
        >
          <PlusCircle className="w-4 h-4" />
          Add Role
        </button>
      </div>

      {/* Role Cards */}
      <div className="grid gap-6">
        {roles.length > 0 ? (
          roles.map((role) => (
            <div 
              key={role.id}
              className="bg-white dark:bg-gray-800 rounded-lg shadow-md overflow-hidden"
            >
              <div className="p-6">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                    {role.name}
                  </h2>
                  <div className="flex items-center gap-2">
                    <button
                      onClick={() => handleEditRole(role)}
                      className="px-3 py-1.5 text-sm text-blue-600 hover:text-blue-700 border border-blue-600 rounded-md hover:bg-blue-50 dark:text-blue-400 dark:border-blue-400 dark:hover:bg-blue-900/20"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => deleteRole(role.id)}
                      className="px-3 py-1.5 text-sm text-red-600 hover:text-red-700 border border-red-600 rounded-md hover:bg-red-50 dark:text-red-400 dark:border-red-400 dark:hover:bg-red-900/20"
                    >
                      Delete
                    </button>
                  </div>
                </div>
                <div className="mt-4">
                  <PermissionMatrix
                    role={role}
                    onUpdate={(permissions) =>
                      updateRole({ ...role, permissions })
                    }
                  />
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4 text-blue-800 dark:text-blue-200">
            No roles have been created yet. Click the "Add Role" button to create your first role.
          </div>
        )}
      </div>

      {/* Role Modal */}
      <RoleModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        role={selectedRole}
        onSave={handleSaveRole}
      />
    </div>
  );
};

export default RoleList;
