import React from 'react';
import { SunIcon, MoonIcon } from 'lucide-react';
import { useTheme } from '../../context/ThemeContext';

const ThemeToggle = () => {
  const { isDark, toggleTheme } = useTheme();

  return (
    <button
      onClick={toggleTheme}
      className="w-full flex items-center px-4 py-2 text-gray-300 hover:text-blue-400 transition-colors duration-200 rounded-lg hover:bg-gray-700/50 dark:hover:bg-gray-800/50"
      aria-label="Toggle theme"
    >
      <div className="relative h-5 w-14 mr-3">
        {/* Sliding Background */}
        <div className="absolute inset-0 rounded-full bg-gray-700 dark:bg-gray-800" />
        
        {/* Sliding Tablet */}
        <div 
          className={`
            absolute top-0 w-5 h-5 rounded-full bg-blue-400
            shadow-lg transform transition-transform duration-300 ease-in-out
            flex items-center justify-center
            ${isDark ? 'translate-x-9' : 'translate-x-0'}
          `}
        >
          {isDark ? (
            <MoonIcon className="h-3 w-3 text-gray-900" />
          ) : (
            <SunIcon className="h-3 w-3 text-yellow-200" />
          )}
        </div>
      </div>
      <span className="font-medium">Theme</span>
    </button>
  );
};

export default ThemeToggle;
