import React, { useState, useEffect } from 'react';
import Modal from '../common/Modal';

const RoleModal = ({ isOpen, onClose, role, onSave }) => {
  const [formData, setFormData] = useState({
    name: '',
    permissions: [],
  });

  useEffect(() => {
    if (role) {
      setFormData(role);
    } else {
      setFormData({
        name: '',
        permissions: [],
      });
    }
  }, [role]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title={role ? 'Edit Role' : 'Add Role'}
    >
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Role Name Input */}
        <div>
          <label className="block text-sm font-medium text-gray-900 dark:text-gray-100">
            Role Name
          </label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            className="mt-2 block w-full rounded-md border-gray-300 shadow-sm focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600 dark:focus:ring-blue-600"
            required
            placeholder="Enter role name"
          />
        </div>

        {/* Permissions Section */}
        <div>
          <label className="block text-sm font-medium text-gray-900 dark:text-gray-100">
            Permissions
          </label>
          <select
            multiple
            value={formData.permissions}
            onChange={(e) => {
              const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
              setFormData({ ...formData, permissions: selectedOptions });
            }}
            className="mt-2 block w-full rounded-md border-gray-300 shadow-sm focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600 dark:focus:ring-blue-600"
          >
            <option value="read">Read</option>
            <option value="write">Write</option>
            <option value="delete">Delete</option>
            {/* Add more permission options here */}
          </select>
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end space-x-3">
          <button
            type="button"
            onClick={onClose}
            className="px-6 py-2 text-gray-700 dark:text-gray-300 border rounded-md hover:bg-gray-50 dark:hover:bg-gray-600 focus:outline-none"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 dark:bg-blue-500 dark:hover:bg-blue-600 focus:outline-none"
          >
            {role ? 'Update' : 'Create'}
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default RoleModal;
