import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, Lock } from 'lucide-react';
import Layout from './components/layout/Layout';
import UserList from './components/users/UserList';
import RoleList from './components/roles/RoleList';
import { AuthProvider } from './context/AuthContext';
import { ThemeProvider } from './context/ThemeContext';

const SecurityMessage = ({ message, complete }) => (
  <div className="flex items-center space-x-2 text-gray-600">
    {complete ? (
      <CheckCircle className="w-5 h-5 text-green-500" />
    ) : (
      <div className="w-5 h-5 animate-spin rounded-full border-2 border-gray-300 border-t-blue-500" />
    )}
    <span className={complete ? "text-green-500" : ""}>{message}</span>
  </div>
);

const SplashScreen = () => {
  const [securityChecks, setSecurityChecks] = useState([
    { message: 'Initializing security protocols...', complete: false },
    { message: 'Verifying system integrity...', complete: false },
    { message: 'Establishing secure connection...', complete: false },
    { message: 'Security checks complete', complete: false }
  ]);

  useEffect(() => {
    securityChecks.forEach((_, index) => {
      setTimeout(() => {
        setSecurityChecks(prev =>
          prev.map((check, i) => ({
            ...check,
            complete: i <= index
          }))
        );
      }, (index + 1) * 500);
    });
  }, []);

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-white dark:bg-gray-900">
      <div className="flex flex-col items-center space-y-8 p-8 rounded-lg">
        {/* Logo Animation */}
        <div className="relative">
          <Shield className="w-24 h-24 text-blue-500 animate-pulse" />
          <Lock className="w-8 h-8 text-blue-700 absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2" />
        </div>

        {/* Welcome Text */}
        <h1 className="text-3xl font-bold text-gray-800 dark:text-white">
          Welcome to SecureShield
        </h1>

        {/* Security Messages */}
        <div className="space-y-3">
          {securityChecks.map((check, index) => (
            <SecurityMessage
              key={index}
              message={check.message}
              complete={check.complete}
            />
          ))}
        </div>

        {/* Loading Indicator */}
        <div className="mt-6">
          <div className="w-16 h-1 bg-blue-200 rounded-full overflow-hidden">
            <div className="w-full h-full bg-blue-500 animate-loading-bar" />
          </div>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <ThemeProvider>
      <AuthProvider>
        {loading ? (
          <SplashScreen />
        ) : (
          <Layout>
            <div className="container mx-auto px-4 py-8">
              <UserList />
              <RoleList />
            </div>
          </Layout>
        )}
      </AuthProvider>
    </ThemeProvider>
  );
};

export default App;
