import React from 'react';
import {
  Table as TableRoot,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";

const Table = ({ columns, data }) => {
  return (
    <div className="relative w-full overflow-auto">
      <TableRoot>
        <TableHeader>
          <TableRow>
            {columns.map((column) => (
              <TableHead
                key={column.accessor}
                className="font-medium"
              >
                {column.header}
              </TableHead>
            ))}
          </TableRow>
        </TableHeader>
        <TableBody>
          {data.map((item, index) => (
            <TableRow
              key={item.id || index}
              className="hover:bg-gray-50 dark:hover:bg-gray-800"
            >
              {columns.map((column) => (
                <TableCell
                  key={`${item.id}-${column.accessor}`}
                >
                  {column.cell ? column.cell(item) : item[column.accessor]}
                </TableCell>
              ))}
            </TableRow>
          ))}
        </TableBody>
      </TableRoot>
    </div>
  );
};

export default Table;
