import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, Lock } from 'lucide-react';
import Layout from './components/layout/Layout';
import UserList from './components/users/UserList';
import RoleList from './components/roles/RoleList';
import { AuthProvider } from './context/AuthContext';
import { ThemeProvider } from './context/ThemeContext';


const SecurityMessage = ({ message, complete }) => (
  <div className="flex items-center space-x-3 text-white">
    <div className={`transition-all duration-300 ${complete ? 'scale-100' : 'scale-0'}`}>
      <CheckCircle className="w-5 h-5" />
    </div>
    <span className={`text-lg ${complete ? 'opacity-100' : 'opacity-70'}`}>
      {message}
    </span>
  </div>
);

const SplashScreen = () => {
  const [securityChecks, setSecurityChecks] = useState([
    { message: 'Initializing security protocols...', complete: false },
    { message: 'Verifying system integrity...', complete: false },
    { message: 'Establishing secure connection...', complete: false },
    { message: 'Security checks complete', complete: false }
  ]);

  useEffect(() => {
    securityChecks.forEach((_, index) => {
      setTimeout(() => {
        setSecurityChecks(prev =>
          prev.map((check, i) => ({
            ...check,
            complete: i <= index
          }))
        );
      }, (index + 1) * 500);
    });
  }, []);

  return (
    <div className="fixed inset-0 bg-gradient-to-br from-blue-900 to-blue-700 flex flex-col items-center justify-center">
      {/* Logo Animation */}
      <div className="mb-8 relative">
        <div className="absolute inset-0 animate-ping opacity-50">
          <Shield className="w-16 h-16 text-blue-400" />
        </div>
        <Shield className="w-16 h-16 text-white relative z-10 animate-pulse" />
      </div>

      {/* Welcome Text */}
      <div className="text-white text-4xl font-bold mb-8 animate-fadeIn">
        Welcome to SecureShield
      </div>

      {/* Security Messages and Loading Indicator Container */}
      <div className="flex flex-col items-center space-y-8 max-w-md w-full px-6">
        {/* Security Messages */}
        <div className="space-y-3">
          {securityChecks.map((check, index) => (
            <SecurityMessage
              key={index}
              message={check.message}
              complete={check.complete}
            />
          ))}
        </div>

        {/* Loading Indicator */}
        <div className="flex items-center space-x-2">
          <Lock className="w-6 h-5 text-white animate-pulse" />
          <div className="w-64 h-1 bg-blue-300 rounded-full overflow-hidden">
            <div className="h-full bg-white animate-progressBar" />
          </div>
        </div>

      </div>

    </div>
  );
};

const App = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000); // Show splash screen for 3 seconds

    return () => clearTimeout(timer);
  }, []);

  return (
    <ThemeProvider>
      <AuthProvider>
        <div className="relative">
          {loading ? (
            <SplashScreen />
          ) : (
            <div className="animate-fadeIn">
              <Layout>
                <div className="p-6">
                  <UserList />
                  <RoleList />
                </div>
              </Layout>
            </div>
          )}
        </div>
      </AuthProvider>
    </ThemeProvider>
  );
};

export default App;
