import React, { useState } from 'react';
import { UserIcon, ChevronDownIcon } from '@heroicons/react/24/outline';

const Header = ({ onProfileClick }) => {
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);

  const toggleProfileDropdown = () => {
    setIsProfileDropdownOpen(!isProfileDropdownOpen);
  };

  return (
    <header className="bg-gradient-to-r from-white to-gray-50 dark:from-gray-900 dark:to-gray-800 shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-8 py-3.5 flex justify-between items-center relative">
        {/* Dashboard Name */}
        <div className="flex-1 text-center md:text-left ml-4">
          <h1 className="text-2xl sm:text-3xl font-bold text-gray-700 dark:text-gray-200">
            Admin Dashboard
          </h1>
        </div>

        {/* Profile Section */}
        <div className="flex items-center">
          <button
            onClick={toggleProfileDropdown}
            className="flex items-center space-x-2 relative"
          >
            <UserIcon className="w-5 h-5 text-gray-700 dark:text-gray-200" />
            <ChevronDownIcon
              className={`w-4 h-4 transition-transform ${
                isProfileDropdownOpen ? 'rotate-180' : ''
              }`}
            />
          </button>

          {/* Profile Dropdown */}
          {isProfileDropdownOpen && (
            <div className="absolute top-full right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-lg shadow-lg z-50">
              <button
                onClick={() => {
                  onProfileClick();
                  setIsProfileDropdownOpen(false);
                }}
                className="w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                Profile
              </button>
              <button
                onClick={() => {
                  // Logout logic
                  setIsProfileDropdownOpen(false);
                }}
                className="w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
