import React from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon } from '@heroicons/react/24/outline';
import ThemeToggle from '../theme/ThemeToggle';

const Sidebar = () => {
  const menuItems = [
    { name: 'ALL', icon: null, isGroup: true, items: ['Users', 'Roles'] }, // New ALL item
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  return (
    <div className="bg-gray-800 dark:bg-gray-900 w-64 min-h-screen text-white flex flex-col">
      <div className="p-4">
        <h2 className="text-2xl font-bold text-gray-100 dark:text-white">RBAC Admin</h2>
      </div>
      <nav className="flex-1">
        {menuItems.map((item) => {
          if (item.isGroup) {
            // If the item is a group (ALL), show it without an icon and add the grouped items
            return (
              <div key={item.name}>
                <a
                  href={`#${item.name.toLowerCase()}`}
                  className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800 font-semibold"
                >
                  {item.name}
                </a>
                {/* Render grouped items like Users and Roles under ALL */}
                <div className="ml-6">
                  {item.items.map((subItem) => (
                    <a
                      key={subItem}
                      href={`#${subItem.toLowerCase()}`}
                      className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
                    >
                      {subItem}
                    </a>
                  ))}
                </div>
              </div>
            );
          }
          return (
            <a
              key={item.name}
              href={`#${item.name.toLowerCase()}`}
              className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
            >
              {item.icon && <item.icon className="h-6 w-6 mr-3" />}
              {item.name}
            </a>
          );
        })}
      </nav>
      <div className="p-4 border-t border-gray-700 dark:border-gray-800">
        <ThemeToggle />
      </div>
    </div>
  );
};

export default Sidebar;
