import React, { useState, useEffect } from 'react';
import {
  UsersIcon,
  ShieldCheckIcon,
  Cog6ToothIcon,
  ArrowLeftOnRectangleIcon,
} from '@heroicons/react/24/outline';
import ThemeToggle from '../theme/ThemeToggle';
import Header from './Header'; // Import the new Header component

const Sidebar = () => {
  const [activeMenu, setActiveMenu] = useState('users');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 1024);

  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 1024;
      setIsMobile(mobile);
      
      // Close mobile menu when switching to desktop
      if (!mobile) {
        setIsMobileMenuOpen(false);
      }
    };

    // Add resize listener
    window.addEventListener('resize', handleResize);
    
    // Initial check
    handleResize();

    // Cleanup listener
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleMenuToggle = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleMenuSelect = (menu) => {
    setActiveMenu(menu);
    if (isMobile) {
      setIsMobileMenuOpen(false);
    }
  };

  const handleLogout = () => {
    // Implement logout logic
    console.log('Logging out');
  };

  const menuItems = [
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  const renderMenuItems = () =>
    menuItems.map((item) => {
      const IconComponent = item.icon;
      const isActive = activeMenu === item.name.toLowerCase();

      return (
        <button
          key={item.name}
          onClick={() => handleMenuSelect(item.name.toLowerCase())}
          className={`w-full flex items-center px-6 py-3.5 text-gray-300 transition-all duration-200 hover:bg-gray-700/50 dark:hover:bg-gray-800/50 group ${
            isActive
              ? 'bg-gray-700/50 dark:bg-gray-800/50 border-r-4 border-blue-400'
              : ''
          }`}
        >
          <IconComponent
            className={`h-5 w-5 mr-3 transition-colors duration-200 ${
              isActive ? 'text-blue-400' : 'group-hover:text-blue-400'
            }`}
          />
          <span
            className={`font-medium ${
              isActive ? 'text-blue-400' : 'group-hover:text-blue-400'
            }`}
          >
            {item.name}
          </span>
        </button>
      );
    });

  return (
    <div className="flex">
      {/* Header for mobile and desktop */}
      <Header onMenuToggle={handleMenuToggle} />

      {/* Sidebar */}
      <div
        className={`fixed lg:relative left-0 top-16 # Top position adjusted to be below header
        w-64 min-h-screen 
        bg-gradient-to-b from-gray-800 to-gray-900 dark:from-gray-900 dark:to-black 
        text-white 
        flex flex-col 
        shadow-xl 
        transform 
        ${isMobile && !isMobileMenuOpen ? '-translate-x-full' : 'translate-x-0'}
        lg:translate-x-0 
        transition-transform 
        duration-300 
        ease-in-out
        z-40`}
      >
        {/* Header - Remove for mobile as we now have a separate Header component */}
        <div className="p-5 border-b border-gray-700/50 flex items-center justify-between">
          <h1 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-400">
            SecureShield
          </h1>
        </div>

        {/* Navigation */}
        <nav className="flex-1 pt-4">{renderMenuItems()}</nav>

        {/* Footer */}
        <div className="p-4 border-t border-gray-700/50 space-y-4">
          <div className="px-4">
            <ThemeToggle />
          </div>

          <button
            onClick={handleLogout}
            className="w-full flex items-center px-4 py-2 text-gray-300 hover:text-red-400 transition-colors duration-200 rounded-lg hover:bg-gray-700/50 dark:hover:bg-gray-800/50"
          >
            <ArrowLeftOnRectangleIcon className="h-5 w-5 mr-3" />
            <span className="font-medium">Logout</span>
          </button>
        </div>
      </div>

      {/* Overlay for mobile menu */}
      {isMobile && isMobileMenuOpen && (
        <div
          onClick={handleMenuToggle}
          className="fixed inset-0 bg-black/50 z-30"
        />
      )}
    </div>
  );
};

export default Sidebar;
