import React, { useState } from 'react';
import RoleModal from './RoleModal';
import PermissionMatrix from './PermissionMatrix';
import { useRoles } from '../../hooks/useRoles';
import { Shield, Plus, Edit2, Trash2, ChevronDown, Lock, Menu, X } from 'lucide-react';

const RoleList = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedRole, setSelectedRole] = useState(null);
  const [expandedRoles, setExpandedRoles] = useState({});
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const { roles, addRole, updateRole, deleteRole } = useRoles();

  const toggleRoleExpand = (roleId) => {
    setExpandedRoles(prev => ({
      ...prev,
      [roleId]: !prev[roleId]
    }));
  };

  const getRoleColor = (roleName) => {
    const colors = {
      admin: 'bg-purple-100 dark:bg-purple-900/30 text-purple-800 dark:text-purple-300',
      moderator: 'bg-blue-100 dark:bg-blue-900/30 text-blue-800 dark:text-blue-300',
      editor: 'bg-green-100 dark:bg-green-900/30 text-green-800 dark:text-green-300',
      user: 'bg-gray-100 dark:bg-gray-800/60 text-gray-800 dark:text-gray-300'
    };
    return colors[roleName.toLowerCase()] || 'bg-gray-100 dark:bg-gray-800/60 text-gray-800 dark:text-gray-300';
  };

  const renderAddRoleButton = (className = '') => (
    <button
      onClick={() => {
        setSelectedRole(null);
        setIsModalOpen(true);
        setIsMobileMenuOpen(false);
      }}
      className={`flex items-center gap-2 px-4 py-2 bg-blue-600 dark:bg-blue-500 text-white 
                 rounded-md hover:bg-blue-700 dark:hover:bg-blue-600 transition-colors duration-200 ${className}`}
    >
      <Plus className="w-4 h-4" />
      Add Role
    </button>
  );

  return (
    <div className="space-y-6 relative">
      {/* Mobile Header with Menu Button */}
      <div className="sm:hidden bg-white dark:bg-gray-900 p-4 flex justify-between items-center">
        <div className="flex items-center gap-2">
          <Shield className="w-6 h-6 text-blue-600 dark:text-blue-500" />
          <h2 className="text-xl font-bold text-gray-900 dark:text-gray-100">
            Roles & Permissions
          </h2>
        </div>
        <button 
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          className="text-gray-600 dark:text-gray-300"
        >
          {isMobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
        </button>
      </div>

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div className="sm:hidden fixed inset-0 bg-white dark:bg-gray-900 z-50 p-6 overflow-y-auto">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-2xl font-bold text-gray-900 dark:text-gray-100">
              Roles Management
            </h3>
            <button 
              onClick={() => setIsMobileMenuOpen(false)}
              className="text-gray-600 dark:text-gray-300"
            >
              <X className="w-6 h-6" />
            </button>
          </div>
          {renderAddRoleButton('w-full mb-4')}
          <p className="text-sm text-gray-500 dark:text-gray-400 mb-4">
            Manage role-based access control and permissions for your organization
          </p>
        </div>
      )}

      {/* Desktop Header */}
      <div className="hidden sm:block bg-white dark:bg-gray-900 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
        <div className="flex flex-wrap justify-between items-center gap-4">
          <div>
            <h2 className="text-2xl font-bold text-gray-900 dark:text-gray-100 flex items-center gap-2">
              <Shield className="w-6 h-6 text-blue-600 dark:text-blue-500" />
              Roles & Permissions
            </h2>
            <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
              Manage role-based access control and permissions for your organization
            </p>
          </div>
          {renderAddRoleButton()}
        </div>
      </div>

      {/* Role List */}
      <div className="bg-white dark:bg-gray-900 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 divide-y divide-gray-200 dark:divide-gray-700">
        {roles.length > 0 ? (
          roles.map((role) => (
            <div key={role.id} className="group">
              <div className="p-4 hover:bg-gray-50 dark:hover:bg-gray-800/50 transition-colors duration-200">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
                  <div className="flex items-center gap-3 w-full">
                    <Lock className="w-5 h-5 text-gray-400 dark:text-gray-500" />
                    <div className="flex-grow">
                      <div className="flex flex-col sm:flex-row sm:items-center gap-2">
                        <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                          {role.name}
                        </h3>
                        <span className={`${getRoleColor(role.name)} text-sm px-2.5 py-0.5 rounded-full self-start sm:self-auto`}>
                          {role.permissions?.length || 0} permissions
                        </span>
                      </div>
                      <p className="text-sm text-gray-500 dark:text-gray-400 mt-1 sm:mt-0">
                        {role.description || `Permissions and access rights for ${role.name}`}
                      </p>
                    </div>
                  </div>
                  
                  <div className="flex items-center gap-2 self-end">
                    <button
                      onClick={() => {
                        setSelectedRole(role);
                        setIsModalOpen(true);
                      }}
                      className="p-2 text-gray-500 hover:text-blue-600 dark:text-gray-400 
                               dark:hover:text-blue-400 transition-colors duration-200"
                    >
                      <Edit2 className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => deleteRole(role.id)}
                      className="p-2 text-gray-500 hover:text-red-600 dark:text-gray-400 
                               dark:hover:text-red-400 transition-colors duration-200"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => toggleRoleExpand(role.id)}
                      className="p-2 text-gray-500 hover:text-gray-700 dark:text-gray-400 
                               dark:hover:text-gray-300 transition-colors duration-200"
                    >
                      <ChevronDown 
                        className={`w-4 h-4 transform transition-transform duration-200 
                                  ${expandedRoles[role.id] ? 'rotate-180' : ''}`}
                      />
                    </button>
                  </div>
                </div>
                
                {/* Permission Matrix - Collapsible */}
                <div className={`mt-4 transition-all duration-200 overflow-hidden
                              ${expandedRoles[role.id] ? 'opacity-100' : 'opacity-0 h-0'}`}>
                  {expandedRoles[role.id] && (
                    <div className="border-t border-gray-200 dark:border-gray-700 pt-4">
                      <PermissionMatrix
                        role={role}
                        onUpdate={(permissions) => updateRole({ ...role, permissions })}
                      />
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="p-8 text-center">
            <Shield className="mx-auto h-12 w-12 text-gray-400 dark:text-gray-600" />
            <h3 className="mt-2 text-lg font-medium text-gray-900 dark:text-gray-100">No roles defined</h3>
            <p className="text-sm text-gray-500 dark:text-gray-400">Create a role to get started</p>
          </div>
        )}
      </div>

      {/* Role Modal */}
      <RoleModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        selectedRole={selectedRole}
        onSave={(role) => {
          if (selectedRole) {
            updateRole(role);
          } else {
            addRole(role);
          }
          setIsModalOpen(false);
        }}
      />
    </div>
  );
};

export default RoleList;
