import React from 'react';
import { useState, useEffect } from 'react';
import { Sun, Moon } from 'lucide-react';

const ThemeToggle = () => {
  const [isDark, setIsDark] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);

  const toggleTheme = () => {
    setIsAnimating(true);
    setIsDark(!isDark);
    // Reset animation state after animation completes
    setTimeout(() => setIsAnimating(false), 500);
  };

  return (
    <button
      onClick={toggleTheme}
      className={`
        relative p-3 rounded-full
        ${isDark ? 'bg-slate-800' : 'bg-blue-50'}
        transition-all duration-500 ease-in-out
        hover:scale-110 focus:outline-none focus:ring-2 focus:ring-blue-400
        shadow-lg
      `}
      aria-label="Toggle theme"
    >
      <div className="relative w-6 h-6">
        <div
          className={`
            absolute inset-0 transform transition-transform duration-500
            ${isAnimating ? (isDark ? 'rotate-180' : '-rotate-180') : 'rotate-0'}
          `}
        >
          {isDark ? (
            <Moon
              className="w-6 h-6 text-yellow-300"
              strokeWidth={2}
            />
          ) : (
            <Sun
              className="w-6 h-6 text-orange-400"
              strokeWidth={2}
            />
          )}
        </div>
      </div>
      <div
        className={`
          absolute inset-0 rounded-full
          ${isDark ? 'bg-gradient-to-r from-indigo-900 to-purple-900' : 'bg-gradient-to-r from-blue-400 to-sky-400'}
          opacity-20 transition-opacity duration-500
        `}
      />
    </button>
  );
};

export default ThemeToggle;
