import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, Lock, Menu, X } from 'lucide-react';

// Layout Component with Responsive Sidebar
const Layout = ({ children }) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  return (
    <div className="flex min-h-screen">
      {/* Mobile Sidebar Toggle */}
      <button 
        className="md:hidden fixed top-4 left-4 z-50 p-2 bg-blue-500 text-white rounded-md"
        onClick={() => setIsSidebarOpen(!isSidebarOpen)}
      >
        {isSidebarOpen ? <X /> : <Menu />}
      </button>

      {/* Sidebar */}
      <div className={`
        fixed md:relative z-40 
        w-64 bg-gray-100 
        transform transition-transform duration-300
        ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        md:translate-x-0 
        h-full md:block
      `}>
        <nav className="p-4 space-y-4">
          <h2 className="text-xl font-bold mb-4">SecureShield</h2>
          <a href="#" className="block py-2 hover:bg-blue-100">Dashboard</a>
          <a href="#" className="block py-2 hover:bg-blue-100">Users</a>
          <a href="#" className="block py-2 hover:bg-blue-100">Roles</a>
        </nav>
      </div>

      {/* Overlay for mobile sidebar */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 bg-black opacity-50 md:hidden z-30"
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      {/* Main Content */}
      <main className="flex-1 p-4 md:p-8 bg-gray-50 overflow-y-auto">
        {children}
      </main>
    </div>
  );
};

// User List Component with Responsive Design
const UserList = () => {
  const [users, setUsers] = useState([
    { id: 1, name: 'John Doe', email: 'john@example.com', role: 'Admin' },
    { id: 2, name: 'Jane Smith', email: 'jane@example.com', role: 'User' }
  ]);

  return (
    <div className="mb-8">
      <h2 className="text-2xl font-bold mb-4">User Management</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {users.map(user => (
          <div 
            key={user.id} 
            className="bg-white shadow-md rounded-lg p-4 hover:shadow-lg transition-shadow"
          >
            <h3 className="text-lg font-semibold">{user.name}</h3>
            <p className="text-gray-600">{user.email}</p>
            <span className="text-sm bg-blue-100 text-blue-800 px-2 py-1 rounded mt-2 inline-block">
              {user.role}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

// Role List Component with Responsive Design
const RoleList = () => {
  const [roles, setRoles] = useState([
    { id: 1, name: 'Administrator', description: 'Full system access' },
    { id: 2, name: 'User', description: 'Limited access' }
  ]);

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Role Management</h2>
      <div className="overflow-x-auto">
        <table className="w-full bg-white shadow-md rounded-lg">
          <thead className="bg-gray-100">
            <tr>
              <th className="p-3 text-left hidden md:table-cell">ID</th>
              <th className="p-3 text-left">Name</th>
              <th className="p-3 text-left">Description</th>
            </tr>
          </thead>
          <tbody>
            {roles.map(role => (
              <tr key={role.id} className="border-b hover:bg-gray-50">
                <td className="p-3 hidden md:table-cell">{role.id}</td>
                <td className="p-3">{role.name}</td>
                <td className="p-3">{role.description}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Security Message Component
const SecurityMessage = ({ message, complete }) => (
  <div className="flex items-center space-x-3 text-white">
    <div className={`transition-all duration-300 ${complete ? 'scale-100' : 'scale-0'}`}>
      <CheckCircle className="w-5 h-5" />
    </div>
    <span className={`text-lg md:text-xl ${complete ? 'opacity-100' : 'opacity-70'}`}>
      {message}
    </span>
  </div>
);

// Splash Screen with Responsive Design
const SplashScreen = () => {
  const [securityChecks, setSecurityChecks] = useState([
    { message: 'Initializing security protocols...', complete: false },
    { message: 'Verifying system integrity...', complete: false },
    { message: 'Establishing secure connection...', complete: false },
    { message: 'Security checks complete', complete: false }
  ]);

  useEffect(() => {
    securityChecks.forEach((_, index) => {
      setTimeout(() => {
        setSecurityChecks(prev =>
          prev.map((check, i) => ({
            ...check,
            complete: i <= index
          }))
        );
      }, (index + 1) * 500);
    });
  }, []);

  return (
    <div className="fixed inset-0 bg-gradient-to-br from-blue-900 to-blue-700 flex flex-col items-center justify-center p-4">
      {/* Logo Animation */}
      <div className="mb-6 md:mb-8 relative">
        <div className="absolute inset-0 animate-ping opacity-50">
          <Shield className="w-12 h-12 md:w-16 md:h-16 text-blue-400" />
        </div>
        <Shield className="w-12 h-12 md:w-16 md:h-16 text-white relative z-10 animate-pulse" />
      </div>

      {/* Welcome Text */}
      <div className="text-2xl md:text-4xl font-bold mb-6 md:mb-8 text-center animate-fadeIn">
        Welcome to SecureShield
      </div>

      {/* Security Messages and Loading Indicator Container */}
      <div className="flex flex-col items-center space-y-6 max-w-md w-full px-4">
        {/* Security Messages */}
        <div className="space-y-3 w-full">
          {securityChecks.map((check, index) => (
            <SecurityMessage
              key={index}
              message={check.message}
              complete={check.complete}
            />
          ))}
        </div>

        {/* Loading Indicator */}
        <div className="flex items-center space-x-2 w-full">
          <Lock className="w-5 h-5 md:w-6 md:h-6 text-white animate-pulse" />
          <div className="flex-1 h-1 bg-blue-300 rounded-full overflow-hidden">
            <div className="h-full bg-white animate-progressBar" />
          </div>
        </div>
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="relative">
      {loading ? (
        <SplashScreen />
      ) : (
        <div className="animate-fadeIn">
          <Layout>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <UserList />
              <RoleList />
            </div>
          </Layout>
        </div>
      )}
    </div>
  );
};

export default App;
