import React, { useState } from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon, SunIcon, MoonIcon } from '@heroicons/react/24/outline';

const Sidebar = () => {
  const [isDarkMode, setIsDarkMode] = useState(true);

  const menuItems = [
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  const toggleTheme = () => {
    setIsDarkMode((prevMode) => !prevMode);
    document.documentElement.classList.toggle('dark', !isDarkMode); // Tailwind's dark mode toggle
  };

  return (
    <div className={`${isDarkMode ? 'bg-gray-800 text-white' : 'bg-gray-200 text-gray-900'} w-64 min-h-screen`}>
      <div className="p-4">
        <h2 className="text-2xl font-bold">RBAC Admin</h2>
      </div>
      <nav className="mt-8">
        {menuItems.map((item) => (
          <a
            key={item.name}
            href={`#${item.name.toLowerCase()}`}
            className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:text-gray-600 dark:hover:bg-gray-300"
          >
            <item.icon className="h-6 w-6 mr-3" />
            {item.name}
          </a>
        ))}
      </nav>
      <div className="absolute bottom-4 left-4">
        <button
          onClick={toggleTheme}
          className="flex items-center px-4 py-2 rounded-lg border border-gray-500 hover:bg-gray-700 dark:hover:bg-gray-300"
        >
          {isDarkMode ? (
            <>
              <SunIcon className="h-5 w-5 mr-2" />
              <span>Light Mode</span>
            </>
          ) : (
            <>
              <MoonIcon className="h-5 w-5 mr-2" />
              <span>Dark Mode</span>
            </>
          )}
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
