import React from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon, ArrowLeftOnRectangleIcon } from '@heroicons/react/24/outline';
import ThemeToggle from '../theme/ThemeToggle';

const Sidebar = ({ onMenuSelect, activeMenu, onLogout }) => {
  const menuItems = [
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  return (
    <div className="bg-gradient-to-b from-gray-800 to-gray-900 dark:from-gray-900 dark:to-black w-64 min-h-screen text-white flex flex-col shadow-xl">
      {/* Header */}
      <div className="p-5 border-b border-gray-700/50">
        <h2 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-400">
          RBAC Admin
        </h2>
      </div>

      {/* Navigation */}
      <nav className="flex-1 pt-4">
        {menuItems.map((item) => {
          const IconComponent = item.icon;
          const isActive = activeMenu === item.name.toLowerCase();
          
          return (
            <button
              key={item.name}
              onClick={() => onMenuSelect(item.name.toLowerCase())}
              className={`w-full flex items-center px-6 py-3.5 text-gray-300 transition-all duration-200 hover:bg-gray-700/50 dark:hover:bg-gray-800/50 group ${
                isActive
                  ? 'bg-gray-700/50 dark:bg-gray-800/50 border-r-4 border-blue-400'
                  : ''
              }`}
            >
              <IconComponent 
                className={`h-5 w-5 mr-3 transition-colors duration-200 ${
                  isActive ? 'text-blue-400' : 'group-hover:text-blue-400'
                }`} 
              />
              <span className={`font-medium ${isActive ? 'text-blue-400' : 'group-hover:text-blue-400'}`}>
                {item.name}
              </span>
            </button>
          );
        })}
      </nav>

      {/* Footer */}
      <div className="p-4 border-t border-gray-700/50 space-y-4">
        <ThemeToggle />
        
        <button
          onClick={onLogout}
          className="w-full flex items-center px-4 py-2 text-gray-300 hover:text-red-400 transition-colors duration-200 rounded-lg hover:bg-gray-700/50 dark:hover:bg-gray-800/50"
        >
          <ArrowLeftOnRectangleIcon className="h-5 w-5 mr-3" />
          <span className="font-medium">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
