import React, { useState, useEffect } from 'react';
import Modal from '../common/Modal';
import { ShieldCheckIcon, KeyIcon } from '@heroicons/react/24/outline';

const RoleModal = ({ isOpen, onClose, role, onSave }) => {
  // Define permission categories
  const permissionCategories = [
    { name: 'Users', key: 'users' },
    { name: 'Roles', key: 'roles' },
    { name: 'Settings', key: 'settings' }
  ];

  const permissionTypes = [
    { name: 'Read', key: 'read' },
    { name: 'Write', key: 'write' },
    { name: 'Delete', key: 'delete' }
  ];

  const [formData, setFormData] = useState({
    name: '',
    permissions: {},
  });

  useEffect(() => {
    if (role) {
      setFormData(role);
    } else {
      // Initialize empty permissions object with all categories
      const initialPermissions = {};
      permissionCategories.forEach(category => {
        initialPermissions[category.key] = {
          read: false,
          write: false,
          delete: false
        };
      });
      setFormData({
        name: '',
        permissions: initialPermissions
      });
    }
  }, [role]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  const togglePermission = (category, type) => {
    setFormData(prev => ({
      ...prev,
      permissions: {
        ...prev.permissions,
        [category]: {
          ...prev.permissions[category],
          [type]: !prev.permissions[category]?.[type]
        }
      }
    }));
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title={role ? 'Edit Role' : 'Add Role'}
    >
      <div className="p-6 bg-white dark:bg-gray-800 rounded-lg">
        <div className="flex items-center mb-6">
          <ShieldCheckIcon className="h-8 w-8 text-blue-500 dark:text-blue-400 mr-3" />
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
            {role ? 'Edit Role' : 'Create New Role'}
          </h2>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Role Name Input */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
              Role Name
            </label>
            <div className="relative">
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg 
                          bg-white dark:bg-gray-700 text-gray-900 dark:text-white
                          focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400 
                          focus:border-transparent transition duration-200"
                placeholder="Enter role name"
                required
              />
            </div>
          </div>

          {/* Permissions Matrix */}
          <div className="space-y-4">
            <div className="flex items-center mb-2">
              <KeyIcon className="h-5 w-5 text-gray-500 dark:text-gray-400 mr-2" />
              <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                Permissions
              </span>
            </div>

            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
                <thead className="bg-gray-50 dark:bg-gray-700">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                      Resource
                    </th>
                    {permissionTypes.map(type => (
                      <th key={type.key} className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                        {type.name}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
                  {permissionCategories.map((category) => (
                    <tr key={category.key}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-700 dark:text-gray-300">
                        {category.name}
                      </td>
                      {permissionTypes.map(type => (
                        <td key={type.key} className="px-6 py-4 whitespace-nowrap">
                          <label className="inline-flex items-center">
                            <input
                              type="checkbox"
                              checked={formData.permissions[category.key]?.[type.key] || false}
                              onChange={() => togglePermission(category.key, type.key)}
                              className="h-4 w-4 text-blue-500 dark:text-blue-400 
                                       border-gray-300 dark:border-gray-600 
                                       rounded focus:ring-blue-500 dark:focus:ring-blue-400
                                       cursor-pointer"
                            />
                          </label>
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex justify-end space-x-3 pt-4 border-t border-gray-200 dark:border-gray-700">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 
                        bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-600 
                        rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700 
                        focus:outline-none focus:ring-2 focus:ring-gray-500 dark:focus:ring-gray-400 
                        transition duration-200"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 text-sm font-medium text-white 
                        bg-blue-600 dark:bg-blue-500 rounded-lg 
                        hover:bg-blue-700 dark:hover:bg-blue-600 
                        focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400 
                        transition duration-200"
            >
              {role ? 'Update Role' : 'Create Role'}
            </button>
          </div>
        </form>
      </div>
    </Modal>
  );
};

export default RoleModal;
