import React from 'react';
import { useState } from 'react';
import { Sun, Moon } from 'lucide-react';

const TabletThemeToggle = () => {
  const [isDark, setIsDark] = useState(false);

  const toggleTheme = () => {
    setIsDark(!isDark);
  };

  return (
    <button
      onClick={toggleTheme}
      className={`
        relative w-20 h-10
        rounded-full
        ${isDark ? 'bg-slate-800' : 'bg-blue-100'}
        transition-colors duration-500
        shadow-lg
        hover:shadow-xl
        focus:outline-none focus:ring-2 focus:ring-blue-400
      `}
      aria-label="Toggle theme"
    >
      {/* Background gradients */}
      <div
        className={`
          absolute inset-0 rounded-full
          ${isDark 
            ? 'bg-gradient-to-r from-slate-900 to-slate-800' 
            : 'bg-gradient-to-r from-blue-100 to-blue-50'
          }
          transition-all duration-500
        `}
      />

      {/* Sliding pill */}
      <div
        className={`
          absolute top-1 
          h-8 w-8
          rounded-full
          transform transition-all duration-500 ease-in-out
          ${isDark ? 'translate-x-11 bg-slate-700' : 'translate-x-1 bg-white'}
          shadow-md
        `}
      >
        {/* Icons container */}
        <div className="relative w-full h-full">
          {/* Sun icon */}
          <div
            className={`
              absolute inset-0 flex items-center justify-center
              transform transition-all duration-500
              ${isDark ? 'opacity-0 rotate-90' : 'opacity-100 rotate-0'}
            `}
          >
            <Sun className="w-5 h-5 text-orange-400" strokeWidth={2} />
          </div>
          
          {/* Moon icon */}
          <div
            className={`
              absolute inset-0 flex items-center justify-center
              transform transition-all duration-500
              ${isDark ? 'opacity-100 rotate-0' : 'opacity-0 -rotate-90'}
            `}
          >
            <Moon className="w-5 h-5 text-yellow-300" strokeWidth={2} />
          </div>
        </div>
      </div>

      {/* Static sun icon */}
      <div className="absolute left-2 top-2.5 opacity-30">
        <Sun className="w-5 h-5 text-orange-400" strokeWidth={2} />
      </div>

      {/* Static moon icon */}
      <div className="absolute right-2 top-2.5 opacity-30">
        <Moon className="w-5 h-5 text-yellow-300" strokeWidth={2} />
      </div>
    </button>
  );
};

export default TabletThemeToggle;
