import React from 'react';
import { useAuth } from '../../context/AuthContext';
import { SunIcon, MoonIcon } from '@heroicons/react/24/outline';

const Header = ({ isDarkMode, toggleTheme }) => {
  const { user, logout } = useAuth();

  return (
    <header className={`${isDarkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'} shadow`}>
      <div className="px-4 py-3 flex justify-between items-center">
        <h1 className="text-xl font-semibold">VRV Security RBAC Dashboard</h1>
        <div className="flex items-center space-x-4">
          <span className="text-gray-600 dark:text-gray-400">{user?.name}</span>
          <button
            onClick={logout}
            className="text-gray-600 hover:text-gray-800 dark:hover:text-white"
          >
            Logout
          </button>
          <button
            onClick={toggleTheme}
            className="flex items-center p-2 rounded-lg border border-gray-500 hover:bg-gray-200 dark:hover:bg-gray-700"
          >
            {isDarkMode ? (
              <SunIcon className="h-5 w-5 text-yellow-400" />
            ) : (
              <MoonIcon className="h-5 w-5 text-gray-600" />
            )}
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
