import React, { useState, useEffect } from 'react';
import Modal from '../common/Modal';
import { ShieldCheckIcon, KeyIcon } from '@heroicons/react/24/outline';

const RoleModal = ({ isOpen, onClose, role, onSave }) => {
  const [formData, setFormData] = useState({
    name: '',
    permissions: [], // This will store selected permissions
  });

  const [showPermissions, setShowPermissions] = useState(false);

  useEffect(() => {
    if (role) {
      setFormData(role); // Load existing role if editing
    } else {
      setFormData({
        name: '',
        permissions: [],
      });
    }
  }, [role]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  const togglePermission = (permission) => {
    setFormData(prev => ({
      ...prev,
      permissions: prev.permissions.includes(permission)
        ? prev.permissions.filter(p => p !== permission)
        : [...prev.permissions, permission]
    }));
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title={role ? 'Edit Role' : 'Add Role'}
    >
      <div className="p-6 bg-white dark:bg-gray-800 rounded-lg">
        <div className="flex items-center mb-6">
          <ShieldCheckIcon className="h-8 w-8 text-blue-500 dark:text-blue-400 mr-3" />
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
            {role ? 'Edit Role' : 'Create New Role'}
          </h2>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Role Name Input */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
              Role Name
            </label>
            <div className="relative">
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg 
                          bg-white dark:bg-gray-700 text-gray-900 dark:text-white
                          focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400 
                          focus:border-transparent transition duration-200"
                placeholder="Enter role name"
                required
              />
            </div>
          </div>

          {/* Permissions Section */}
          <div className="space-y-2">
            <button
              type="button"
              onClick={() => setShowPermissions(!showPermissions)}
              className="flex items-center justify-between w-full px-4 py-2 
                        bg-gray-50 dark:bg-gray-700 rounded-lg
                        hover:bg-gray-100 dark:hover:bg-gray-600 
                        transition duration-200"
            >
              <div className="flex items-center">
                <KeyIcon className="h-5 w-5 text-gray-500 dark:text-gray-400 mr-2" />
                <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  Permissions
                </span>
              </div>
              <span className="text-sm text-gray-500 dark:text-gray-400">
                {formData.permissions.length} selected
              </span>
            </button>

            {showPermissions && (
              <div className="mt-2 p-4 bg-gray-50 dark:bg-gray-700/50 rounded-lg space-y-2">
                {['Read', 'Write', 'Delete'].map((permission) => (
                  <label
                    key={permission}
                    className="flex items-center p-2 hover:bg-gray-100 dark:hover:bg-gray-600 rounded cursor-pointer"
                  >
                    <input
                      type="checkbox"
                      checked={formData.permissions.includes(permission)}
                      onChange={() => togglePermission(permission)}
                      className="h-4 w-4 text-blue-500 dark:text-blue-400 
                                border-gray-300 dark:border-gray-600 
                                rounded focus:ring-blue-500 dark:focus:ring-blue-400"
                    />
                    <span className="ml-3 text-sm text-gray-700 dark:text-gray-300">
                      {permission}
                    </span>
                  </label>
                ))}
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="flex justify-end space-x-3 pt-4 border-t border-gray-200 dark:border-gray-700">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 
                        bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-600 
                        rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700 
                        focus:outline-none focus:ring-2 focus:ring-gray-500 dark:focus:ring-gray-400 
                        transition duration-200"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 text-sm font-medium text-white 
                        bg-blue-600 dark:bg-blue-500 rounded-lg 
                        hover:bg-blue-700 dark:hover:bg-blue-600 
                        focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400 
                        transition duration-200"
            >
              {role ? 'Update Role' : 'Create Role'}
            </button>
          </div>
        </form>
      </div>
    </Modal>
  );
};

export default RoleModal;
