import React from 'react';
import { useAuth } from '../../context/AuthContext';

const Header = () => {
  const { user, logout } = useAuth();

  return (
    <header className="bg-white dark:bg-gray-900 shadow-md dark:shadow-gray-800">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <h1 className="text-2xl font-semibold text-gray-900 dark:text-white">
            VRV Security RBAC Dashboard
          </h1>
        </div>
        
        <div className="flex items-center space-x-6">
          <span className="text-lg text-gray-700 dark:text-gray-300">{user?.name}</span>

          {/* Red Logout Button */}
          <button
            onClick={logout}
            className="bg-red-600 text-white hover:bg-red-700 rounded-lg px-4 py-2 text-sm transition duration-200 transform hover:scale-105 dark:bg-red-500 dark:hover:bg-red-600 dark:text-gray-200"
          >
            Logout
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
