import React from 'react';
import { useAuth } from '../../context/AuthContext';
import { UserIcon } from '@heroicons/react/outline'; // Import the user icon from Heroicons

const Header = () => {
  const { user, logout } = useAuth();

  return (
    <header className="bg-white dark:bg-gray-900 shadow-md dark:shadow-gray-800">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <h1 className="text-2xl font-semibold text-gray-900 dark:text-white">
            Security Dashboard
          </h1>
        </div>

        <div className="flex items-center space-x-6">
          {/* Profile Icon */}
          <div className="relative">
            <button
              onClick={() => alert('Profile clicked')} // You can add your logic for profile here
              className="bg-gray-300 p-2 rounded-full text-gray-600 hover:bg-gray-400 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600 transition duration-200"
            >
              <UserIcon className="w-6 h-6" />
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
