import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, Lock } from 'lucide-react';

const SecurityMessage = ({ message, complete }) => (
  <div className={`flex items-center space-x-2 transition-all duration-500 ${complete ? 'text-green-500' : 'text-gray-400'}`}>
    <CheckCircle className={`w-5 h-5 transition-opacity duration-300 ${complete ? 'opacity-100' : 'opacity-0'}`} />
    <span className="text-sm font-medium">{message}</span>
  </div>
);

const SplashScreen = () => {
  const [securityChecks, setSecurityChecks] = useState([
    { message: 'Initializing security protocols...', complete: false },
    { message: 'Verifying system integrity...', complete: false },
    { message: 'Establishing secure connection...', complete: false },
    { message: 'Security checks complete', complete: false }
  ]);

  useEffect(() => {
    securityChecks.forEach((_, index) => {
      setTimeout(() => {
        setSecurityChecks(prev =>
          prev.map((check, i) => ({
            ...check,
            complete: i <= index
          }))
        );
      }, (index + 1) * 800);
    });
  }, []);

  return (
    <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center p-4">
      {/* Logo Animation */}
      <div className="relative mb-8">
        <Shield className="w-20 h-20 text-blue-500 animate-pulse" />
        <Lock className="w-8 h-8 text-white absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2" />
      </div>

      {/* Welcome Text */}
      <h1 className="text-3xl font-bold text-white mb-8 animate-fade-in">
        Welcome to SecureShield
      </h1>

      {/* Security Messages */}
      <div className="space-y-3 mb-8 w-72">
        {securityChecks.map((check, index) => (
          <SecurityMessage
            key={index}
            message={check.message}
            complete={check.complete}
          />
        ))}
      </div>

      {/* Loading Indicator */}
      <div className="relative w-48 h-2 bg-gray-700 rounded-full overflow-hidden">
        <div className="absolute top-0 left-0 h-full bg-blue-500 rounded-full animate-progress" />
      </div>
    </div>
  );
};

const App = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 4000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100">
      {loading ? (
        <SplashScreen />
      ) : (
        <div className="p-6">
          <h1 className="text-2xl font-bold mb-4">Welcome to the Dashboard</h1>
          {/* Your main app content here */}
        </div>
      )}
    </div>
  );
};

// Add these styles to your global CSS or Tailwind config
const style = document.createElement('style');
style.textContent = `
  @keyframes progress {
    0% { width: 0%; }
    100% { width: 100%; }
  }
  @keyframes fade-in {
    0% { opacity: 0; }
    100% { opacity: 1; }
  }
  .animate-progress {
    animation: progress 4s linear;
  }
  .animate-fade-in {
    animation: fade-in 1s ease-in-out;
  }
`;
document.head.appendChild(style);

export default App;
