import React, { useState } from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon } from '@heroicons/react/24/outline';
import ThemeToggle from '../theme/ThemeToggle';

const Sidebar = () => {
  const [activeMenu, setActiveMenu] = useState('all'); // State to track the active menu

  const menuItems = [
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  const handleMenuClick = (menu) => {
    setActiveMenu(menu);
  };

  return (
    <div className="bg-gray-800 dark:bg-gray-900 w-64 min-h-screen text-white flex flex-col">
      <div className="p-4">
        <h2 className="text-2xl font-bold text-gray-100 dark:text-white">RBAC Admin</h2>
      </div>
      <nav className="flex-1">
        {/* All option */}
        <a
          href="#all"
          onClick={() => handleMenuClick('all')}
          className={`flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800 ${activeMenu === 'all' ? 'bg-gray-700 dark:bg-gray-800' : ''}`}
        >
          <UsersIcon className="h-6 w-6 mr-3" />
          All
        </a>

        {/* Conditionally render Users and Roles based on the active menu */}
        {(activeMenu === 'all' || activeMenu === 'users') && (
          <a
            href="#users"
            onClick={() => handleMenuClick('users')}
            className={`flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800 ${activeMenu === 'users' ? 'bg-gray-700 dark:bg-gray-800' : ''}`}
          >
            <UsersIcon className="h-6 w-6 mr-3" />
            Users
          </a>
        )}

        {(activeMenu === 'all' || activeMenu === 'roles') && (
          <a
            href="#roles"
            onClick={() => handleMenuClick('roles')}
            className={`flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800 ${activeMenu === 'roles' ? 'bg-gray-700 dark:bg-gray-800' : ''}`}
          >
            <ShieldCheckIcon className="h-6 w-6 mr-3" />
            Roles
          </a>
        )}

        {/* Settings option */}
        <a
          href="#settings"
          onClick={() => handleMenuClick('settings')}
          className={`flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800 ${activeMenu === 'settings' ? 'bg-gray-700 dark:bg-gray-800' : ''}`}
        >
          <Cog6ToothIcon className="h-6 w-6 mr-3" />
          Settings
        </a>
      </nav>
      <div className="p-4 border-t border-gray-700 dark:border-gray-800">
        <ThemeToggle />
      </div>
    </div>
  );
};

export default Sidebar;
