import React, { useState, useEffect } from 'react';
import Modal from '../common/Modal';

const RoleModal = ({ isOpen, onClose, role, onSave }) => {
  const [formData, setFormData] = useState({
    name: '',
    permissions: [],
  });

  useEffect(() => {
    if (role) {
      setFormData(role);
    } else {
      setFormData({
        name: '',
        permissions: [],
      });
    }
  }, [role]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title={role ? 'Edit Role' : 'Add Role'}
    >
      <form onSubmit={handleSubmit} className="space-y-6 p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg">
        <div>
          <label className="block text-sm font-medium text-gray-900 dark:text-gray-300">
            Role Name
          </label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            className="mt-2 block w-full rounded-md border-gray-300 shadow-sm focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-600"
            required
          />
        </div>
        <div className="flex justify-between items-center">
          <div className="flex items-center space-x-2">
            {/* Optionally, you can add checkboxes for permissions */}
            <label className="block text-sm font-medium text-gray-900 dark:text-gray-300">
              Permissions
            </label>
            <select
              multiple
              value={formData.permissions}
              onChange={(e) => {
                const options = Array.from(e.target.selectedOptions, (option) => option.value);
                setFormData({ ...formData, permissions: options });
              }}
              className="mt-2 w-full rounded-md border-gray-300 shadow-sm focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-600"
            >
              <option value="read">Read</option>
              <option value="write">Write</option>
              <option value="delete">Delete</option>
            </select>
          </div>
        </div>
        <div className="flex justify-end space-x-4">
          <button
            type="button"
            onClick={onClose}
            className="px-4 py-2 text-gray-900 dark:text-gray-300 border rounded-md hover:bg-gray-100 dark:hover:bg-gray-700"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 dark:bg-blue-500 dark:hover:bg-blue-600"
          >
            {role ? 'Update' : 'Create'}
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default RoleModal;
