import React, { useState } from 'react';
import Table from '../common/Table';
import UserModal from './UserModal';
import UserFilter from './UserFilter';
import { useUsers } from '../../hooks/useUsers';

const UserList = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const { users, addUser, updateUser, deleteUser } = useUsers();

  // States for search term, role, and status filters
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRole, setSelectedRole] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('');

  const columns = [
    { header: 'Name', accessor: 'name' },
    { header: 'Email', accessor: 'email' },
    { header: 'Role', accessor: 'role' },
    { header: 'Status', accessor: 'status' },
    {
      header: 'Actions',
      accessor: 'actions',
      cell: (user) => (
        <div className="flex justify-center space-x-4 -ml-2 mr-2"> {/* Added right margin (mr-2) */}
          <button
            onClick={() => {
              setSelectedUser(user);
              setIsModalOpen(true);
            }}
            className="text-blue-600 hover:text-blue-800"
          >
            Edit
          </button>
          <button
            onClick={() => deleteUser(user.id)}
            className="text-red-600 hover:text-red-800 mr-1"
          >
            Delete
          </button>
        </div>
      ),
    },
  ];

  // Handle search input change
  const handleSearch = (searchTerm) => {
    setSearchTerm(searchTerm);
  };

  // Handle role filter change
  const handleRoleChange = (role) => {
    setSelectedRole(role);
  };

  // Handle status filter change
  const handleStatusChange = (status) => {
    setSelectedStatus(status);
  };

  // Filter users based on search term, role, and status
  const filteredUsers = users.filter((user) => {
    const matchesSearch = user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          user.role.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          user.status.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesRole = selectedRole ? user.role.toLowerCase() === selectedRole.toLowerCase() : true;
    const matchesStatus = selectedStatus ? user.status.toLowerCase() === selectedStatus.toLowerCase() : true;

    return matchesSearch && matchesRole && matchesStatus;
  });

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Users</h2>
        <button
          onClick={() => {
            setSelectedUser(null);
            setIsModalOpen(true);
          }}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 dark:bg-blue-500 dark:hover:bg-blue-600"
        >
          Add User
        </button>
      </div>
      {/* Pass the filter handlers as props */}
      <UserFilter 
        onSearch={handleSearch} 
        onRoleChange={handleRoleChange} 
        onStatusChange={handleStatusChange} 
      />
      <div className="bg-white dark:bg-gray-900 rounded-lg shadow">
        {filteredUsers.length === 0 ? (
          <div className="text-center py-4 text-gray-600 dark:text-gray-400">
            No results found
          </div>
        ) : (
          <Table columns={columns} data={filteredUsers} />
        )}
      </div>
      <UserModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        user={selectedUser}
        onSave={(user) => {
          if (user.id) {
            updateUser(user);
          } else {
            addUser(user);
          }
          setIsModalOpen(false);
        }}
      />
    </div>
  );
};

export default UserList;
