import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Layout from './components/layout/Layout';
import UserList from './components/users/UserList';
import RoleList from './components/roles/RoleList';
import { AuthProvider } from './context/AuthContext';
import { Users, Shield, Key } from 'lucide-react'; // Importing icons from lucide-react
import './index.css'; // or wherever your Tailwind styles are

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <Layout>
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/users" element={<UserList />} />
            <Route path="/roles" element={<RoleList />} />
          </Routes>
        </Layout>
      </Router>
    </AuthProvider>
  );
};

const Dashboard = () => {
  return (
    <div className="space-y-6">
      <h1 className="text-2xl font-bold">Dashboard</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <DashboardCard
          title="Total Users"
          value="156"
          icon={Users} // Corrected icon reference
          trend="+12%"
          trendUp={true}
        />
        <DashboardCard
          title="Active Roles"
          value="8"
          icon={Shield} // Corrected icon reference
          trend="+2"
          trendUp={true}
        />
        <DashboardCard
          title="Permissions"
          value="24"
          icon={Key} // Corrected icon reference
          trend="0"
          trendUp={null}
        />
      </div>
    </div>
  );
};

const DashboardCard = ({ title, value, icon: Icon, trend, trendUp }) => {
  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <div className="flex justify-between items-start">
        <div>
          <p className="text-gray-500 text-sm">{title}</p>
          <h3 className="text-2xl font-bold mt-1">{value}</h3>
        </div>
        <div className="p-2 bg-blue-100 rounded-lg">
          <Icon className="h-6 w-6 text-blue-600" />
        </div>
      </div>
      {trend && (
        <div className="mt-4 flex items-center">
          <span
            className={`text-sm ${
              trendUp ? 'text-green-600' : trendUp === false ? 'text-red-600' : 'text-gray-600'
            }`}
          >
            {trend}
          </span>
          <span className="text-gray-500 text-sm ml-1">vs last month</span>
        </div>
      )}
    </div>
  );
};

export default App;
