import React, { useState } from 'react';
import { Plus, Search, Filter, Users } from 'lucide-react';
import Table from '../common/Table';
import UserModal from './UserModal';
import UserFilter from './UserFilter';
import { useUsers } from '../../hooks/useUsers';
import { Card, CardHeader, CardTitle, CardDescription, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";

const UserList = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const { users, addUser, updateUser, deleteUser } = useUsers();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRole, setSelectedRole] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('');

  const getStatusColor = (status) => {
    const statusColors = {
      active: 'bg-green-100 text-green-800',
      inactive: 'bg-gray-100 text-gray-800',
      pending: 'bg-yellow-100 text-yellow-800',
      suspended: 'bg-red-100 text-red-800'
    };
    return statusColors[status.toLowerCase()] || 'bg-gray-100 text-gray-800';
  };

  const getRoleBadgeVariant = (role) => {
    const roleVariants = {
      admin: 'bg-purple-100 text-purple-800',
      user: 'bg-blue-100 text-blue-800',
      editor: 'bg-indigo-100 text-indigo-800'
    };
    return roleVariants[role.toLowerCase()] || 'bg-gray-100 text-gray-800';
  };

  const columns = [
    { 
      header: 'Name', 
      accessor: 'name',
      cell: (user) => (
        <div className="flex items-center gap-3">
          <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center">
            {user.name.charAt(0).toUpperCase()}
          </div>
          <div>
            <div className="font-medium">{user.name}</div>
            <div className="text-sm text-gray-500">{user.email}</div>
          </div>
        </div>
      )
    },
    { 
      header: 'Role', 
      accessor: 'role',
      cell: (user) => (
        <Badge className={`${getRoleBadgeVariant(user.role)} capitalize`}>
          {user.role}
        </Badge>
      )
    },
    { 
      header: 'Status', 
      accessor: 'status',
      cell: (user) => (
        <Badge className={`${getStatusColor(user.status)} capitalize`}>
          {user.status}
        </Badge>
      )
    },
    {
      header: 'Actions',
      accessor: 'actions',
      cell: (user) => (
        <div className="flex justify-end space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => {
              setSelectedUser(user);
              setIsModalOpen(true);
            }}
            className="hover:bg-gray-100 transition-colors"
          >
            Edit
          </Button>
          <Button
            variant="destructive"
            size="sm"
            onClick={() => deleteUser(user.id)}
            className="hover:bg-red-600 transition-colors"
          >
            Delete
          </Button>
        </div>
      ),
    },
  ];

  const filteredUsers = users.filter((user) => {
    const matchesSearch = user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          user.role.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          user.status.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesRole = selectedRole ? user.role.toLowerCase() === selectedRole.toLowerCase() : true;
    const matchesStatus = selectedStatus ? user.status.toLowerCase() === selectedStatus.toLowerCase() : true;

    return matchesSearch && matchesRole && matchesStatus;
  });

  return (
    <Card className="w-full">
      <CardHeader>
        <div className="flex justify-between items-center">
          <div>
            <CardTitle className="text-2xl font-bold flex items-center gap-2">
              <Users className="w-6 h-6" />
              User Management
            </CardTitle>
            <CardDescription>
              Manage your team members and their account permissions here
            </CardDescription>
          </div>
          <Button
            onClick={() => {
              setSelectedUser(null);
              setIsModalOpen(true);
            }}
            className="flex items-center gap-2"
          >
            <Plus className="w-4 h-4" />
            Add User
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          <UserFilter 
            onSearch={setSearchTerm}
            onRoleChange={setSelectedRole}
            onStatusChange={setSelectedStatus}
          />
          
          {filteredUsers.length === 0 ? (
            <Alert>
              <AlertDescription className="flex items-center justify-center py-8 text-gray-500">
                No users found matching your search criteria
              </AlertDescription>
            </Alert>
          ) : (
            <div className="rounded-md border">
              <Table 
                columns={columns} 
                data={filteredUsers}
              />
            </div>
          )}
        </div>
      </CardContent>

      <UserModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        user={selectedUser}
        onSave={(user) => {
          if (user.id) {
            updateUser(user);
          } else {
            addUser(user);
          }
          setIsModalOpen(false);
        }}
      />
    </Card>
  );
};

export default UserList;
