import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, Lock, Menu, X } from 'lucide-react';

// Mobile-friendly Layout Component
const Layout = ({ children }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  return (
    <div className="min-h-screen flex flex-col">
      {/* Mobile Header */}
      <header className="md:hidden bg-blue-800 p-4 flex justify-between items-center">
        <div className="flex items-center space-x-2">
          <Shield className="w-8 h-8 text-white" />
          <span className="text-white font-bold text-xl">SecureShield</span>
        </div>
        <button 
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          className="text-white"
        >
          {isMobileMenuOpen ? <X /> : <Menu />}
        </button>
      </header>

      {/* Mobile Sidebar */}
      {isMobileMenuOpen && (
        <nav className="md:hidden fixed inset-0 bg-blue-900 z-50 pt-16">
          <div className="px-4 space-y-4">
            <a href="#users" className="block text-white text-xl py-2">Users</a>
            <a href="#roles" className="block text-white text-xl py-2">Roles</a>
            <button 
              onClick={() => setIsMobileMenuOpen(false)}
              className="w-full bg-blue-700 text-white py-2 rounded"
            >
              Close Menu
            </button>
          </div>
        </nav>
      )}

      {/* Desktop Layout */}
      <div className="flex flex-1">
        {/* Desktop Sidebar */}
        <nav className="hidden md:block w-64 bg-blue-800 p-6">
          <div className="flex items-center space-x-3 mb-8">
            <Shield className="w-10 h-10 text-white" />
            <span className="text-white font-bold text-2xl">SecureShield</span>
          </div>
          <div className="space-y-4">
            <a href="#users" className="block text-white hover:bg-blue-700 p-2 rounded">Users</a>
            <a href="#roles" className="block text-white hover:bg-blue-700 p-2 rounded">Roles</a>
          </div>
        </nav>

        {/* Main Content Area */}
        <main className="flex-1 bg-gray-50 p-4 md:p-6 overflow-y-auto">
          {children}
        </main>
      </div>
    </div>
  );
};

const SecurityMessage = ({ message, complete }) => (
  <div className="flex items-center space-x-3 text-white">
    <div className={`transition-all duration-300 ${complete ? 'scale-100' : 'scale-0'}`}>
      <CheckCircle className="w-5 h-5 md:w-6 md:h-6" />
    </div>
    <span className={`text-base md:text-lg ${complete ? 'opacity-100' : 'opacity-70'}`}>
      {message}
    </span>
  </div>
);

const SplashScreen = () => {
  const [securityChecks, setSecurityChecks] = useState([
    { message: 'Initializing security protocols...', complete: false },
    { message: 'Verifying system integrity...', complete: false },
    { message: 'Establishing secure connection...', complete: false },
    { message: 'Security checks complete', complete: false }
  ]);

  useEffect(() => {
    securityChecks.forEach((_, index) => {
      setTimeout(() => {
        setSecurityChecks(prev =>
          prev.map((check, i) => ({
            ...check,
            complete: i <= index
          }))
        );
      }, (index + 1) * 500);
    });
  }, []);

  return (
    <div className="fixed inset-0 bg-gradient-to-br from-blue-900 to-blue-700 flex flex-col items-center justify-center p-4">
      {/* Logo Animation */}
      <div className="mb-6 md:mb-8 relative">
        <div className="absolute inset-0 animate-ping opacity-50">
          <Shield className="w-12 h-12 md:w-16 md:h-16 text-blue-400" />
        </div>
        <Shield className="w-12 h-12 md:w-16 md:h-16 text-white relative z-10 animate-pulse" />
      </div>

      {/* Welcome Text */}
      <div className="text-white text-3xl md:text-4xl font-bold mb-6 md:mb-8 text-center animate-fadeIn">
        Welcome to SecureShield
      </div>

      {/* Security Messages and Loading Indicator Container */}
      <div className="flex flex-col items-center space-y-6 md:space-y-8 w-full max-w-md px-4">
        {/* Security Messages */}
        <div className="space-y-3 w-full">
          {securityChecks.map((check, index) => (
            <SecurityMessage
              key={index}
              message={check.message}
              complete={check.complete}
            />
          ))}
        </div>

        {/* Loading Indicator */}
        <div className="flex items-center space-x-2 w-full">
          <Lock className="w-5 h-5 md:w-6 md:h-6 text-white animate-pulse" />
          <div className="flex-1 h-1 bg-blue-300 rounded-full overflow-hidden">
            <div className="h-full bg-white animate-progressBar" />
          </div>
        </div>
      </div>
    </div>
  );
};

// Mock Components for Users and Roles (you would replace these with actual implementations)
const UserList = () => (
  <div id="users" className="bg-white shadow rounded-lg p-4 mb-6">
    <h2 className="text-xl md:text-2xl font-bold mb-4">User Management</h2>
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {[...Array(6)].map((_, index) => (
        <div key={index} className="bg-gray-100 p-3 rounded-md">
          User {index + 1}
        </div>
      ))}
    </div>
  </div>
);

const RoleList = () => (
  <div id="roles" className="bg-white shadow rounded-lg p-4">
    <h2 className="text-xl md:text-2xl font-bold mb-4">Role Management</h2>
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      {[...Array(4)].map((_, index) => (
        <div key={index} className="bg-gray-100 p-3 rounded-md">
          Role {index + 1}
        </div>
      ))}
    </div>
  </div>
);

// Authentication Context (Mock)
const AuthContext = React.createContext(null);
const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  return (
    <AuthContext.Provider value={{ user, setUser }}>
      {children}
    </AuthContext.Provider>
  );
};

// Theme Context (Mock)
const ThemeContext = React.createContext(null);
const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('light');
  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

const App = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000); // Show splash screen for 3 seconds

    return () => clearTimeout(timer);
  }, []);

  return (
    <ThemeProvider>
      <AuthProvider>
        <div className="relative min-h-screen">
          {loading ? (
            <SplashScreen />
          ) : (
            <div className="animate-fadeIn">
              <Layout>
                <div className="space-y-6 p-4 md:p-6">
                  <UserList />
                  <RoleList />
                </div>
              </Layout>
            </div>
          )}
        </div>
      </AuthProvider>
    </ThemeProvider>
  );
};

export default App;
