import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, Lock, Menu, X } from 'lucide-react';

// Layout Component with Mobile Responsiveness
const Layout = ({ children }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Mobile Menu Toggle */}
      <button 
        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        className="fixed top-4 right-4 z-50 md:hidden bg-blue-500 text-white p-2 rounded-full shadow-lg"
      >
        {isMobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
      </button>

      {/* Sidebar - Responsive */}
      <div className={`
        fixed inset-y-0 left-0 transform transition-transform duration-300 
        w-64 bg-white shadow-lg z-40
        ${isMobileMenuOpen ? 'translate-x-0' : '-translate-x-full'}
        md:relative md:translate-x-0
      `}>
        <div className="p-6">
          <div className="flex items-center space-x-3 mb-8">
            <Shield className="w-8 h-8 text-blue-600" />
            <h1 className="text-2xl font-bold text-gray-800">SecureShield</h1>
          </div>
          {/* Add navigation menu items here */}
          <nav className="space-y-2">
            <a href="#" className="block py-2 px-4 hover:bg-blue-50 rounded">Dashboard</a>
            <a href="#" className="block py-2 px-4 hover:bg-blue-50 rounded">Users</a>
            <a href="#" className="block py-2 px-4 hover:bg-blue-50 rounded">Roles</a>
          </nav>
        </div>
      </div>

      {/* Main Content Area - Responsive */}
      <main className="flex-1 md:ml-64 p-4 sm:p-6 transition-all">
        <div className="bg-white shadow-md rounded-lg p-6">
          {children}
        </div>
      </main>

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="fixed inset-0 bg-black opacity-50 z-30 md:hidden"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}
    </div>
  );
};

// User List Component - Responsive
const UserList = () => {
  const [users, setUsers] = useState([
    { id: 1, name: 'John Doe', email: 'john@example.com', role: 'Admin' },
    { id: 2, name: 'Jane Smith', email: 'jane@example.com', role: 'User' },
  ]);

  return (
    <div className="mb-8">
      <h2 className="text-2xl font-semibold mb-4">Users</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {users.map(user => (
          <div key={user.id} className="bg-gray-50 p-4 rounded-lg shadow-sm">
            <div className="flex justify-between items-center">
              <div>
                <h3 className="font-bold text-lg">{user.name}</h3>
                <p className="text-gray-600 text-sm">{user.email}</p>
              </div>
              <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded-full text-xs">
                {user.role}
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Role List Component - Responsive
const RoleList = () => {
  const [roles, setRoles] = useState([
    { id: 1, name: 'Admin', permissions: ['Full Access'] },
    { id: 2, name: 'User', permissions: ['Read', 'Limited Edit'] },
  ]);

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Roles</h2>
      <div className="space-y-4">
        {roles.map(role => (
          <div key={role.id} className="bg-gray-50 p-4 rounded-lg shadow-sm">
            <div className="flex justify-between items-center">
              <h3 className="font-bold text-lg">{role.name}</h3>
              <div className="flex space-x-2">
                {role.permissions.map(perm => (
                  <span 
                    key={perm} 
                    className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs"
                  >
                    {perm}
                  </span>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Security Message Component
const SecurityMessage = ({ message, complete }) => (
  <div className="flex items-center space-x-3 text-white">
    <div className={`transition-all duration-300 ${complete ? 'scale-100' : 'scale-0'}`}>
      <CheckCircle className="w-5 h-5" />
    </div>
    <span className={`text-base sm:text-lg ${complete ? 'opacity-100' : 'opacity-70'}`}>
      {message}
    </span>
  </div>
);

// Splash Screen - Responsive
const SplashScreen = () => {
  const [securityChecks, setSecurityChecks] = useState([
    { message: 'Initializing security protocols...', complete: false },
    { message: 'Verifying system integrity...', complete: false },
    { message: 'Establishing secure connection...', complete: false },
    { message: 'Security checks complete', complete: false }
  ]);

  useEffect(() => {
    securityChecks.forEach((_, index) => {
      setTimeout(() => {
        setSecurityChecks(prev =>
          prev.map((check, i) => ({
            ...check,
            complete: i <= index
          }))
        );
      }, (index + 1) * 500);
    });
  }, []);

  return (
    <div className="fixed inset-0 bg-gradient-to-br from-blue-900 to-blue-700 flex flex-col items-center justify-center px-4">
      {/* Logo Animation - Responsive */}
      <div className="mb-6 sm:mb-8 relative">
        <div className="absolute inset-0 animate-ping opacity-50">
          <Shield className="w-12 h-12 sm:w-16 sm:h-16 text-blue-400" />
        </div>
        <Shield className="w-12 h-12 sm:w-16 sm:h-16 text-white relative z-10 animate-pulse" />
      </div>

      {/* Welcome Text - Responsive */}
      <div className="text-white text-3xl sm:text-4xl font-bold mb-6 sm:mb-8 text-center animate-fadeIn">
        Welcome to SecureShield
      </div>

      {/* Security Messages and Loading Indicator Container - Responsive */}
      <div className="flex flex-col items-center space-y-6 max-w-md w-full px-4">
        {/* Security Messages */}
        <div className="space-y-3 w-full">
          {securityChecks.map((check, index) => (
            <SecurityMessage
              key={index}
              message={check.message}
              complete={check.complete}
            />
          ))}
        </div>

        {/* Loading Indicator - Responsive */}
        <div className="flex items-center space-x-2 w-full">
          <Lock className="w-5 h-5 sm:w-6 sm:h-6 text-white animate-pulse" />
          <div className="flex-1 h-1 bg-blue-300 rounded-full overflow-hidden">
            <div className="h-full bg-white animate-progressBar" />
          </div>
        </div>
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000); // Show splash screen for 3 seconds

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="relative">
      {loading ? (
        <SplashScreen />
      ) : (
        <div className="animate-fadeIn">
          <Layout>
            <div className="space-y-8">
              <UserList />
              <RoleList />
            </div>
          </Layout>
        </div>
      )}
    </div>
  );
};

export default App;
