import React, { useState, useEffect } from 'react';
import Modal from '../common/Modal';

const RoleModal = ({ isOpen, onClose, role, onSave }) => {
  const [formData, setFormData] = useState({
    name: '',
    permissions: [],
  });

  useEffect(() => {
    if (role) {
      setFormData(role);
    } else {
      setFormData({
        name: '',
        permissions: [],
      });
    }
  }, [role]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title={role ? 'Edit Role' : 'Add Role'}
    >
      <form onSubmit={handleSubmit} className="space-y-6 p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg">
        <div>
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
            Role Name
          </label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            className="mt-2 block w-full px-4 py-2 rounded-md border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            required
          />
        </div>

        {/* Permissions Section */}
        <div>
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
            Permissions
          </label>
          <div className="mt-2 space-y-2">
            {/* Example checkboxes for permissions */}
            <div className="flex items-center">
              <input
                type="checkbox"
                checked={formData.permissions.includes('view')}
                onChange={(e) => {
                  const permissions = [...formData.permissions];
                  if (e.target.checked) {
                    permissions.push('view');
                  } else {
                    const index = permissions.indexOf('view');
                    permissions.splice(index, 1);
                  }
                  setFormData({ ...formData, permissions });
                }}
                className="h-4 w-4 text-blue-500 border-gray-300 rounded focus:ring-2 focus:ring-blue-500 dark:bg-gray-600 dark:border-gray-500"
              />
              <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">View Access</span>
            </div>
            <div className="flex items-center">
              <input
                type="checkbox"
                checked={formData.permissions.includes('edit')}
                onChange={(e) => {
                  const permissions = [...formData.permissions];
                  if (e.target.checked) {
                    permissions.push('edit');
                  } else {
                    const index = permissions.indexOf('edit');
                    permissions.splice(index, 1);
                  }
                  setFormData({ ...formData, permissions });
                }}
                className="h-4 w-4 text-blue-500 border-gray-300 rounded focus:ring-2 focus:ring-blue-500 dark:bg-gray-600 dark:border-gray-500"
              />
              <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">Edit Access</span>
            </div>
            {/* Add more permissions as needed */}
          </div>
        </div>

        <div className="flex justify-end space-x-4 mt-4">
          <button
            type="button"
            onClick={onClose}
            className="px-4 py-2 text-gray-700 dark:text-gray-200 border border-gray-300 dark:border-gray-600 rounded-md hover:bg-gray-100 dark:hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-blue-500 dark:hover:bg-blue-600"
          >
            {role ? 'Update' : 'Create'}
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default RoleModal;
