import React, { useState } from 'react';
import { UserIcon, Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  return (
    <header className="bg-gradient-to-r from-white to-gray-50 dark:from-gray-900 dark:to-gray-800 shadow-lg relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-8 py-3.5 flex justify-between items-center">
        {/* Mobile Menu Toggle */}
        <button 
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          className="md:hidden absolute left-4 top-1/2 -translate-y-1/2 z-50"
        >
          {isMobileMenuOpen ? <XMarkIcon className="w-6 h-6" /> : <Bars3Icon className="w-6 h-6" />}
        </button>

        {/* Name on the Left */}
        <div className="flex items-center space-x-4 w-full justify-center md:justify-start">
          <h1 className="text-2xl sm:text-3xl font-bold text-gray-700 dark:text-gray-200">
            Admin Dashboard
          </h1>
        </div>

        {/* Profile Icon on the Right */}
        <div className="hidden md:flex items-center">
          <button
            onClick={() => alert('This feature is not available')}
            className="relative group"
          >
            <div className="absolute -inset-0.5 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full opacity-0 group-hover:opacity-100 transition duration-300 blur"></div>
            <div className="relative px-3 py-3 bg-white dark:bg-gray-800 rounded-full flex items-center space-x-2 transition duration-200 group-hover:shadow-lg">
              <UserIcon className="w-5 h-5 text-gray-700 dark:text-gray-200" />
              <span className="hidden sm:inline-block text-sm font-medium text-gray-700 dark:text-gray-200">
                Profile
              </span>
            </div>
          </button>
        </div>
      </div>

      {/* Mobile Menu Dropdown */}
      {isMobileMenuOpen && (
        <div className="md:hidden absolute top-full left-0 w-full bg-white dark:bg-gray-800 shadow-lg z-40">
          <div className="px-4 py-4 flex flex-col items-center space-y-4">
            <button
              onClick={() => {
                alert('This feature is not available');
                setIsMobileMenuOpen(false);
              }}
              className="w-full flex justify-center items-center space-x-2 py-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg"
            >
              <UserIcon className="w-5 h-5 text-gray-700 dark:text-gray-200" />
              <span className="text-sm font-medium text-gray-700 dark:text-gray-200">
                Profile
              </span>
            </button>
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
