import React from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon, CollectionIcon } from '@heroicons/react/24/outline'; // Use CollectionIcon for "All"
import ThemeToggle from '../theme/ThemeToggle';

const Sidebar = ({ onMenuSelect, activeMenu }) => {
  const menuItems = [
    { name: 'All', icon: CollectionIcon }, // Replace ListBulletIcon with CollectionIcon
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  return (
    <div className="bg-gray-800 dark:bg-gray-900 w-64 min-h-screen text-white flex flex-col">
      <div className="p-4">
        <h2 className="text-2xl font-bold text-gray-100 dark:text-white">RBAC Admin</h2>
      </div>
      <nav className="flex-1">
        {menuItems.map((item) => {
          const IconComponent = item.icon;
          return (
            <button
              key={item.name}
              onClick={() => onMenuSelect(item.name.toLowerCase())}
              className={`w-full flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800 ${
                activeMenu === item.name.toLowerCase()
                  ? 'bg-gray-700 dark:bg-gray-800'
                  : ''
              }`}
            >
              <IconComponent className="h-6 w-6 mr-3" />
              {item.name}
            </button>
          );
        })}
      </nav>
      <div className="p-4 border-t border-gray-700 dark:border-gray-800">
        <ThemeToggle />
      </div>
    </div>
  );
};

export default Sidebar;
