import React, { useState } from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon } from '@heroicons/react/24/outline';
import ThemeToggle from '../theme/ThemeToggle';

const Sidebar = () => {
  const [showAll, setShowAll] = useState(false);
  const [activeMenu, setActiveMenu] = useState(''); // Tracks active menu (Users, Roles, All)

  const menuItems = [
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  // Handle menu item clicks
  const handleMenuClick = (menu) => {
    if (menu === 'All') {
      setShowAll(!showAll);
      setActiveMenu('All');
    } else {
      setActiveMenu(menu);
      setShowAll(false); // Hide "All" and show selected menu
    }
  };

  return (
    <div className="bg-gray-800 dark:bg-gray-900 w-64 min-h-screen text-white flex flex-col">
      <div className="p-4">
        <h2 className="text-2xl font-bold text-gray-100 dark:text-white">RBAC Admin</h2>
      </div>
      <nav className="flex-1">
        <a
          onClick={() => handleMenuClick('All')}
          className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800 cursor-pointer"
        >
          <UsersIcon className="h-6 w-6 mr-3" />
          All
        </a>

        {(showAll || activeMenu === 'Users') && (
          <a
            key="users"
            onClick={() => handleMenuClick('Users')}
            className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
          >
            <UsersIcon className="h-6 w-6 mr-3" />
            Users
          </a>
        )}

        {(showAll || activeMenu === 'Roles') && (
          <a
            key="roles"
            onClick={() => handleMenuClick('Roles')}
            className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
          >
            <ShieldCheckIcon className="h-6 w-6 mr-3" />
            Roles
          </a>
        )}

        <a
          key="settings"
          href="#settings"
          className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
        >
          <Cog6ToothIcon className="h-6 w-6 mr-3" />
          Settings
        </a>
      </nav>
      <div className="p-4 border-t border-gray-700 dark:border-gray-800">
        <ThemeToggle />
      </div>
    </div>
  );
};

export default Sidebar;
