import React, { useState } from 'react';
import { UserIcon, ChevronDownIcon } from '@heroicons/react/24/outline';

const Header = ({ onMobileMenuToggle }) => {
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);

  return (
    <header className="bg-gradient-to-r from-white to-gray-50 dark:from-gray-900 dark:to-gray-800 shadow-lg relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-8 py-3.5 flex justify-between items-center">
        {/* Name Section */}
        <div className="flex-1 flex items-center">
          <h1 className="text-2xl sm:text-3xl font-bold text-gray-700 dark:text-gray-200 ml-14 md:ml-0">
            Admin Dashboard
          </h1>
        </div>

        {/* Mobile Profile Dropdown */}
        <div className="md:hidden relative">
          <button 
            onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
            className="flex items-center space-x-2"
          >
            <UserIcon className="w-5 h-5 text-gray-700 dark:text-gray-200" />
            <ChevronDownIcon 
              className={`w-4 h-4 transition-transform ${
                isProfileDropdownOpen ? 'rotate-180' : ''
              }`} 
            />
          </button>

          {isProfileDropdownOpen && (
            <div className="absolute top-full right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-lg shadow-lg z-50">
              <button
                onClick={() => {
                  alert('This feature is not available');
                  setIsProfileDropdownOpen(false);
                }}
                className="w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700 flex items-center space-x-2"
              >
                <UserIcon className="w-4 h-4" />
                <span>Profile</span>
              </button>
            </div>
          )}
        </div>

        {/* Desktop Profile */}
        <div className="hidden md:flex items-center">
          <button
            onClick={() => alert('This feature is not available')}
            className="relative group"
          >
            <div className="absolute -inset-0.5 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full opacity-0 group-hover:opacity-100 transition duration-300 blur"></div>
            <div className="relative px-3 py-3 bg-white dark:bg-gray-800 rounded-full flex items-center space-x-2 transition duration-200 group-hover:shadow-lg">
              <UserIcon className="w-5 h-5 text-gray-700 dark:text-gray-200" />
              <span className="hidden sm:inline-block text-sm font-medium text-gray-700 dark:text-gray-200">
                Profile
              </span>
            </div>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
