import React, { useState } from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon } from '@heroicons/react/24/outline';
import ThemeToggle from '../theme/ThemeToggle';

const Sidebar = () => {
  // State to manage the active selection
  const [activeItem, setActiveItem] = useState('all');

  const menuItems = [
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  // Handle click to change active menu
  const handleClick = (item) => {
    setActiveItem(item);
  };

  return (
    <div className="bg-gray-800 dark:bg-gray-900 w-64 min-h-screen text-white flex flex-col">
      <div className="p-4">
        <h2 className="text-2xl font-bold text-gray-100 dark:text-white">RBAC Admin</h2>
      </div>

      <nav className="flex-1">
        {/* All option */}
        <a
          onClick={() => handleClick('all')}
          className={`flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800 cursor-pointer ${activeItem === 'all' ? 'bg-gray-700 dark:bg-gray-800' : ''}`}
        >
          <span className="h-6 w-6 mr-3">ðŸ”‘</span> {/* You can use a different icon for "All" */}
          All
        </a>

        {/* Show Users and Roles based on activeItem */}
        {activeItem === 'all' && (
          <>
            <a
              key="Users"
              href="#users"
              className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
            >
              <UsersIcon className="h-6 w-6 mr-3" />
              Users
            </a>
            <a
              key="Roles"
              href="#roles"
              className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
            >
              <ShieldCheckIcon className="h-6 w-6 mr-3" />
              Roles
            </a>
          </>
        )}

        {/* Show Users only */}
        {activeItem === 'users' && (
          <a
            key="Users"
            href="#users"
            className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
          >
            <UsersIcon className="h-6 w-6 mr-3" />
            Users
          </a>
        )}

        {/* Show Roles only */}
        {activeItem === 'roles' && (
          <a
            key="Roles"
            href="#roles"
            className="flex items-center px-6 py-3 text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800"
          >
            <ShieldCheckIcon className="h-6 w-6 mr-3" />
            Roles
          </a>
        )}
      </nav>

      <div className="p-4 border-t border-gray-700 dark:border-gray-800">
        <ThemeToggle />
      </div>
    </div>
  );
};

export default Sidebar;
