import React from 'react';
import { useUsers } from '../hooks/useUsers';
import { useRoles } from '../hooks/useRoles';

const Dashboard = () => {
  const { users } = useUsers();
  const { roles } = useRoles();

  return (
    <div className="space-y-6">
      <h1 className="text-2xl font-bold">Dashboard</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Users Overview</h2>
          <p className="text-4xl font-bold text-blue-600">{users.length}</p>
          <p className="text-gray-600">Total Users</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Roles Overview</h2>
          <p className="text-4xl font-bold text-blue-600">{roles.length}</p>
          <p className="text-gray-600">Total Roles</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
