import React, { useState } from 'react';
import RoleModal from './RoleModal';
import PermissionMatrix from './PermissionMatrix';
import { useRoles } from '../../hooks/useRoles';
import { 
  Shield, Plus, Edit2, Trash2, ChevronDown, Lock, Search 
} from 'lucide-react';

const RoleList = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedRole, setSelectedRole] = useState(null);
  const [expandedRoles, setExpandedRoles] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const { roles, addRole, updateRole, deleteRole } = useRoles();

  const toggleRoleExpand = (roleId) => {
    setExpandedRoles(prev => ({
      ...prev,
      [roleId]: !prev[roleId]
    }));
  };

  const getRoleColor = (roleName) => {
    const colors = {
      admin: 'bg-purple-100 dark:bg-purple-900/30 text-purple-800 dark:text-purple-300',
      moderator: 'bg-blue-100 dark:bg-blue-900/30 text-blue-800 dark:text-blue-300',
      editor: 'bg-green-100 dark:bg-green-900/30 text-green-800 dark:text-green-300',
      user: 'bg-gray-100 dark:bg-gray-800/60 text-gray-800 dark:text-gray-300'
    };
    return colors[roleName.toLowerCase()] || 'bg-gray-100 dark:bg-gray-800/60 text-gray-800 dark:text-gray-300';
  };

  const filteredRoles = roles.filter((role) => 
    role.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    role.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto px-4 py-6">
      {/* Header Section */}
      <div className="bg-white dark:bg-gray-900 rounded-lg shadow-md border border-gray-200 dark:border-gray-700 mb-6">
        <div className="p-6 flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
          <div>
            <h2 className="text-2xl font-bold text-gray-900 dark:text-gray-100 flex items-center gap-3 mb-2">
              <Shield className="w-6 h-6 text-blue-600 dark:text-blue-500" />
              Roles Management
            </h2>
            <p className="text-sm text-gray-500 dark:text-gray-400">
              Manage and configure role-based access control
            </p>
          </div>
          <div className="flex flex-col md:flex-row items-stretch md:items-center gap-4 w-full md:w-auto">
            <div className="relative flex-grow">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Search className="w-4 h-4 text-gray-400" />
              </div>
              <input
                type="text"
                placeholder="Search roles..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 
                           rounded-md bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100"
              />
            </div>
            <button
              onClick={() => {
                setSelectedRole(null);
                setIsModalOpen(true);
              }}
              className="flex items-center justify-center gap-2 px-4 py-2 
                         bg-blue-600 dark:bg-blue-500 text-white rounded-md 
                         hover:bg-blue-700 dark:hover:bg-blue-600 
                         transition-colors duration-200"
            >
              <Plus className="w-4 h-4" />
              New Role
            </button>
          </div>
        </div>
      </div>

      {/* Role List */}
      <div className="grid gap-4">
        {filteredRoles.length > 0 ? (
          filteredRoles.map((role) => (
            <div 
              key={role.id} 
              className="bg-white dark:bg-gray-900 rounded-lg shadow-md border border-gray-200 dark:border-gray-700"
            >
              <div className="p-6 flex flex-col md:flex-row justify-between items-start md:items-center">
                <div className="flex items-start md:items-center gap-4 w-full">
                  <Lock className="w-6 h-6 text-gray-400 dark:text-gray-500 mt-1 md:mt-0" />
                  <div className="flex-grow">
                    <div className="flex flex-col md:flex-row md:items-center gap-2 mb-2">
                      <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                        {role.name}
                      </h3>
                      <span className={`${getRoleColor(role.name)} text-sm px-2.5 py-0.5 rounded-full self-start md:self-auto`}>
                        {role.permissions?.length || 0} permissions
                      </span>
                    </div>
                    <p className="text-sm text-gray-500 dark:text-gray-400">
                      {role.description || `Permissions and access rights for ${role.name}`}
                    </p>
                  </div>
                </div>
                
                <div className="flex items-center gap-2 mt-4 md:mt-0 w-full md:w-auto justify-between md:justify-end">
                  <div className="flex gap-2">
                    <button
                      onClick={() => {
                        setSelectedRole(role);
                        setIsModalOpen(true);
                      }}
                      className="p-2 text-gray-500 hover:text-blue-600 dark:text-gray-400 
                                 dark:hover:text-blue-400 transition-colors duration-200"
                    >
                      <Edit2 className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => deleteRole(role.id)}
                      className="p-2 text-gray-500 hover:text-red-600 dark:text-gray-400 
                                 dark:hover:text-red-400 transition-colors duration-200"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => toggleRoleExpand(role.id)}
                      className="p-2 text-gray-500 hover:text-gray-700 dark:text-gray-400 
                                 dark:hover:text-gray-300 transition-colors duration-200"
                    >
                      <ChevronDown 
                        className={`w-4 h-4 transform transition-transform duration-200 
                                    ${expandedRoles[role.id] ? 'rotate-180' : ''}`}
                      />
                    </button>
                  </div>
                </div>
              </div>
                
              {/* Permission Matrix - Collapsible */}
              {expandedRoles[role.id] && (
                <div className="border-t border-gray-200 dark:border-gray-700 p-6">
                  <PermissionMatrix
                    role={role}
                    onUpdate={(permissions) => updateRole({ ...role, permissions })}
                  />
                </div>
              )}
            </div>
          ))
        ) : (
          <div className="bg-white dark:bg-gray-900 rounded-lg shadow-md border border-gray-200 dark:border-gray-700 p-8 text-center">
            <Shield className="mx-auto h-12 w-12 text-gray-400 dark:text-gray-600 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100">No roles found</h3>
            <p className="mt-2 text-sm text-gray-500 dark:text-gray-400 mb-4">
              Get started by creating a new role
            </p>
            <button
              onClick={() => {
                setSelectedRole(null);
                setIsModalOpen(true);
              }}
              className="inline-flex items-center justify-center gap-2 px-4 py-2 
                         bg-blue-600 dark:bg-blue-500 text-white rounded-md 
                         hover:bg-blue-700 dark:hover:bg-blue-600 
                         transition-colors duration-200"
            >
              <Plus className="w-4 h-4" />
              New Role
            </button>
          </div>
        )}
      </div>

      {/* Role Modal */}
      <RoleModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        role={selectedRole}
        onSave={(role) => {
          if (role.id) {
            updateRole(role);
          } else {
            addRole(role);
          }
          setIsModalOpen(false);
        }}
      />
    </div>
  );
};

export default RoleList;
