import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, Lock } from 'lucide-react';
import Layout from './components/layout/Layout';
import UserList from './components/users/UserList';
import RoleList from './components/roles/RoleList';
import { AuthProvider } from './context/AuthContext';
import { ThemeProvider } from './context/ThemeContext';

const SecurityMessage = ({ message, complete }) => (
  <div className="flex items-center space-x-2">
    {message}
  </div>
);

const SplashScreen = () => {
  const [securityChecks, setSecurityChecks] = useState([
    { message: 'Initializing security protocols...', complete: false },
    { message: 'Verifying system integrity...', complete: false },
    { message: 'Establishing secure connection...', complete: false },
    { message: 'Security checks complete', complete: false }
  ]);

  useEffect(() => {
    securityChecks.forEach((_, index) => {
      setTimeout(() => {
        setSecurityChecks(prev =>
          prev.map((check, i) => ({
            ...check,
            complete: i <= index
          }))
        );
      }, (index + 1) * 500);
    });
  }, []);

  return (
    <div className="fixed inset-0 flex items-center justify-center">
      <div className="text-center">
        {/* Logo Animation */}
        <div>
          <Shield />
          <Lock />
        </div>

        {/* Welcome Text */}
        <h1>Welcome to SecureShield</h1>

        {/* Security Messages */}
        <div>
          {securityChecks.map((check, index) => (
            <SecurityMessage
              key={index}
              message={check.message}
              complete={check.complete}
            />
          ))}
        </div>

        {/* Loading Indicator */}
        <div>
          <div />
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <ThemeProvider>
      <AuthProvider>
        {loading ? (
          <SplashScreen />
        ) : (
          <Layout>
            <UserList />
            <RoleList />
          </Layout>
        )}
      </AuthProvider>
    </ThemeProvider>
  );
};

export default App;
