import React, { useState } from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon, ViewGridIcon } from '@heroicons/react/24/outline';
import ThemeToggle from '../theme/ThemeToggle';
import UserList from './UserList';
import RoleList from './RoleList';

// Updated Dashboard Component
const Dashboard = () => {
  const [currentView, setCurrentView] = useState('all');

  const renderContent = () => {
    switch (currentView) {
      case 'all':
        return (
          <div className="space-y-8">
            <UserList />
            <RoleList />
          </div>
        );
      case 'users':
        return <UserList />;
      case 'roles':
        return <RoleList />;
      case 'settings':
        return <div className="p-4">Settings Content</div>;
      default:
        return null;
    }
  };

  return (
    <div className="flex">
      <Sidebar currentView={currentView} onViewChange={setCurrentView} />
      <div className="flex-1 p-8 bg-gray-50 dark:bg-gray-800 min-h-screen">
        {renderContent()}
      </div>
    </div>
  );
};

// Updated Sidebar Component
const Sidebar = ({ currentView, onViewChange }) => {
  const menuItems = [
    { name: 'All', icon: ViewGridIcon, id: 'all' },
    { name: 'Users', icon: UsersIcon, id: 'users' },
    { name: 'Roles', icon: ShieldCheckIcon, id: 'roles' },
    { name: 'Settings', icon: Cog6ToothIcon, id: 'settings' },
  ];

  return (
    <div className="bg-gray-800 dark:bg-gray-900 w-64 min-h-screen text-white flex flex-col">
      <div className="p-4">
        <h2 className="text-2xl font-bold text-gray-100 dark:text-white">RBAC Admin</h2>
      </div>
      <nav className="flex-1">
        {menuItems.map((item) => (
          <button
            key={item.id}
            onClick={() => onViewChange(item.id)}
            className={`w-full flex items-center px-6 py-3 text-left ${
              currentView === item.id
                ? 'bg-gray-700 dark:bg-gray-800 text-white'
                : 'text-gray-300 hover:bg-gray-700 dark:hover:bg-gray-800'
            }`}
          >
            <item.icon className="h-6 w-6 mr-3" />
            {item.name}
          </button>
        ))}
      </nav>
      <div className="p-4 border-t border-gray-700 dark:border-gray-800">
        <ThemeToggle />
      </div>
    </div>
  );
};

export default Sidebar;
