import React from 'react';
import { UsersIcon, ShieldCheckIcon, Cog6ToothIcon, Bars3Icon, ArrowLeftOnRectangleIcon } from 'lucide-react';
import ThemeToggle from '../theme/ThemeToggle';

const Sidebar = ({ onMenuSelect, activeMenu, onLogout }) => {
  const menuItems = [
    { name: 'All', icon: Bars3Icon },
    { name: 'Users', icon: UsersIcon },
    { name: 'Roles', icon: ShieldCheckIcon },
    { name: 'Settings', icon: Cog6ToothIcon },
  ];

  return (
    <div className="bg-gray-900 w-64 min-h-screen text-white flex flex-col shadow-xl relative">
      {/* Header with gradient overlay */}
      <div className="relative p-6 bg-gradient-to-r from-blue-600 to-purple-600">
        <div className="absolute inset-0 bg-black/20" />
        <h2 className="relative text-2xl font-bold text-white tracking-tight">
          RBAC Admin
        </h2>
      </div>

      {/* Navigation */}
      <nav className="flex-1 pt-4">
        {menuItems.map((item) => {
          const IconComponent = item.icon;
          const isActive = activeMenu === item.name.toLowerCase();
          
          return (
            <button
              key={item.name}
              onClick={() => onMenuSelect(item.name.toLowerCase())}
              className={`w-full flex items-center px-6 py-3.5 text-gray-300 
                hover:bg-gray-800/50 transition-all duration-200 ease-in-out
                ${isActive ? 'bg-gray-800 text-white relative' : ''}
                group`}
            >
              {/* Active indicator */}
              {isActive && (
                <div className="absolute left-0 top-0 bottom-0 w-1 bg-blue-500" />
              )}
              
              <IconComponent className={`h-5 w-5 mr-3 transition-transform duration-200
                group-hover:scale-110 ${isActive ? 'text-blue-400' : ''}`} />
              <span className={`font-medium ${isActive ? 'text-white' : ''}`}>
                {item.name}
              </span>
            </button>
          );
        })}
      </nav>

      {/* Footer section with theme toggle and logout */}
      <div className="p-4 border-t border-gray-800 space-y-4">
        <div className="flex items-center justify-between px-2">
          <span className="text-sm text-gray-400">Theme</span>
          <ThemeToggle />
        </div>
        
        <button
          onClick={onLogout}
          className="w-full flex items-center justify-center gap-2 px-4 py-2.5 
            text-gray-300 hover:text-white rounded-lg
            bg-gray-800 hover:bg-gray-700 
            transition-all duration-200 ease-in-out
            border border-gray-700 hover:border-gray-600"
        >
          <ArrowLeftOnRectangleIcon className="h-5 w-5" />
          <span className="font-medium">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
