import React, { useState } from 'react';
import RoleModal from './RoleModal';
import PermissionMatrix from './PermissionMatrix';
import { useRoles } from '../../hooks/useRoles';
import { 
  Shield, 
  Plus, 
  Edit2, 
  Trash2, 
  ChevronDown, 
  Lock, 
  MoreHorizontal 
} from 'lucide-react';

const RoleList = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedRole, setSelectedRole] = useState(null);
  const [expandedRoles, setExpandedRoles] = useState({});
  const { roles, addRole, updateRole, deleteRole } = useRoles();

  const toggleRoleExpand = (roleId) => {
    setExpandedRoles(prev => ({
      ...prev,
      [roleId]: !prev[roleId]
    }));
  };

  const getRoleColor = (roleName) => {
    const colors = {
      admin: 'bg-purple-100 text-purple-800 dark:bg-purple-900/30 dark:text-purple-300',
      moderator: 'bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300',
      editor: 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-300',
      user: 'bg-gray-100 text-gray-800 dark:bg-gray-800/60 dark:text-gray-300'
    };
    return colors[roleName.toLowerCase()] || 'bg-gray-100 text-gray-800 dark:bg-gray-800/60 dark:text-gray-300';
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6">
        <div className="mb-4 md:mb-0">
          <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100 flex items-center gap-2">
            <Shield className="w-6 h-6 text-blue-600 dark:text-blue-500" />
            Role Management
          </h1>
          <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">
            Manage access control and permissions
          </p>
        </div>
        <button
          onClick={() => {
            setSelectedRole(null);
            setIsModalOpen(true);
          }}
          className="flex items-center justify-center gap-2 px-4 py-2 
                     bg-blue-600 text-white rounded-md 
                     hover:bg-blue-700 transition-colors 
                     w-full md:w-auto"
        >
          <Plus className="w-4 h-4" />
          Create Role
        </button>
      </div>

      {/* Role List */}
      <div className="bg-white dark:bg-gray-900 rounded-lg shadow overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50 dark:bg-gray-800 border-b dark:border-gray-700">
              <tr>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  Role Name
                </th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  Permissions
                </th>
                <th className="px-4 py-3 text-right text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
              {roles.map((role) => (
                <React.Fragment key={role.id}>
                  <tr className="hover:bg-gray-50 dark:hover:bg-gray-800/50 transition-colors">
                    <td className="px-4 py-4">
                      <div className="flex items-center">
                        <Lock className="w-5 h-5 mr-3 text-gray-400 dark:text-gray-500" />
                        <div>
                          <div className="text-sm font-medium text-gray-900 dark:text-white">
                            {role.name}
                          </div>
                          <div className="text-sm text-gray-500 dark:text-gray-400">
                            {role.description || 'No description'}
                          </div>
                        </div>
                      </div>
                    </td>
                    <td className="px-4 py-4">
                      <span className={`${getRoleColor(role.name)} text-xs px-2.5 py-0.5 rounded-full`}>
                        {role.permissions?.length || 0} Permissions
                      </span>
                    </td>
                    <td className="px-4 py-4 text-right">
                      <div className="flex justify-end items-center space-x-2">
                        <button
                          onClick={() => {
                            setSelectedRole(role);
                            setIsModalOpen(true);
                          }}
                          className="text-blue-500 hover:text-blue-700 p-1 rounded"
                        >
                          <Edit2 className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => deleteRole(role.id)}
                          className="text-red-500 hover:text-red-700 p-1 rounded"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => toggleRoleExpand(role.id)}
                          className="text-gray-500 hover:text-gray-700 p-1 rounded"
                        >
                          <ChevronDown 
                            className={`w-4 h-4 transform transition-transform 
                                      ${expandedRoles[role.id] ? 'rotate-180' : ''}`} 
                          />
                        </button>
                      </div>
                    </td>
                  </tr>
                  {expandedRoles[role.id] && (
                    <tr>
                      <td colSpan="3" className="bg-gray-50 dark:bg-gray-800/50 p-4">
                        <PermissionMatrix
                          role={role}
                          onUpdate={(permissions) => updateRole({ ...role, permissions })}
                        />
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>

        {/* Empty State */}
        {roles.length === 0 && (
          <div className="text-center py-12 px-4">
            <Shield className="mx-auto h-12 w-12 text-gray-400 dark:text-gray-600" />
            <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-gray-100">
              No Roles Created
            </h3>
            <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
              Get started by creating your first role
            </p>
            <button
              onClick={() => {
                setSelectedRole(null);
                setIsModalOpen(true);
              }}
              className="mt-4 inline-flex items-center px-4 py-2 
                         bg-blue-600 text-white rounded-md 
                         hover:bg-blue-700 transition-colors"
            >
              <Plus className="w-4 h-4 mr-2" />
              Create Role
            </button>
          </div>
        )}
      </div>

      {/* Role Modal */}
      <RoleModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        role={selectedRole}
        onSave={(role) => {
          if (role.id) {
            updateRole(role);
          } else {
            addRole(role);
          }
          setIsModalOpen(false);
        }}
      />
    </div>
  );
};

export default RoleList;
