import React from 'react';
import { UserIcon } from '@heroicons/react/24/outline'; // Correct import for Heroicons v2

const Header = () => {
  return (
    <header className="bg-white dark:bg-gray-900 shadow-md dark:shadow-gray-800">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Title Section */}
        <div className="flex items-center space-x-4">
          <h1 className="text-2xl font-semibold text-gray-900 dark:text-white">
            Security Dashboard
          </h1>
        </div>

        {/* Action Section */}
        <div className="flex items-center space-x-4">
          {/* Example: Add a Search Bar */}
          <div className="hidden sm:flex items-center bg-gray-100 dark:bg-gray-800 px-3 py-2 rounded-md">
            <input
              type="text"
              placeholder="Search"
              className="bg-transparent border-none focus:ring-0 text-sm text-gray-700 dark:text-gray-300"
              aria-label="Search"
            />
          </div>

          {/* Profile Icon */}
          <div className="relative">
            <button
              onClick={() => alert('This feature is not available')}
              className="bg-gray-300 p-2 rounded-full text-gray-600 hover:bg-gray-400 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600 transition duration-200"
              aria-label="User Profile"
              title="User Profile"
            >
              <UserIcon className="w-6 h-6" />
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
