import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { SunIcon, MoonIcon } from '@heroicons/react/24/outline';

const Header = () => {
  const { user, logout } = useAuth();
  const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleTheme = () => {
    setIsDarkMode((prevMode) => !prevMode);
    document.documentElement.classList.toggle('dark', !isDarkMode); // Tailwind's dark mode toggle
  };

  return (
    <header
      className={`${
        isDarkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'
      } shadow`}
    >
      <div className="px-4 py-3 flex justify-between items-center">
        <h1 className="text-xl font-semibold">VRV Security RBAC Dashboard</h1>
        <div className="flex items-center space-x-4">
          <span className="text-gray-600 dark:text-gray-300">{user?.name}</span>
          <button
            onClick={logout}
            className="text-gray-600 dark:text-gray-300 hover:text-gray-800 dark:hover:text-gray-100"
          >
            Logout
          </button>
          <button
            onClick={toggleTheme}
            className="flex items-center px-2 py-1 rounded-lg border border-gray-500 hover:bg-gray-200 dark:hover:bg-gray-700"
          >
            {isDarkMode ? (
              <>
                <SunIcon className="h-5 w-5 mr-1" />
                <span>Light</span>
              </>
            ) : (
              <>
                <MoonIcon className="h-5 w-5 mr-1" />
                <span>Dark</span>
              </>
            )}
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
